import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Footer from '../components/Footer';
import './Terms.css';

function Terms() {
  const [terms, setTerms] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    window.scrollTo(0, 0);
    const fetchTerms = async () => {
      try {
        setLoading(true);
        setError('');
        const response = await axios.get('http://localhost:5001/api/terms', { timeout: 5000 });
        setTerms(response.data);
      } catch (err) {
        console.error('Error fetching terms:', err);
        setError('Failed to load terms. Please try again.');
        setTerms([
          { section: 'Acceptance of Terms', content: 'By using StreamFlix, you agree to abide by these Terms of Use and our Privacy Policy.' },
          { section: 'Subscription Services', content: 'StreamFlix offers monthly subscriptions for accessing movie recommendations and streaming content. You may cancel anytime via your account settings.' },
          { section: 'Content Usage', content: 'Movies and recommendations provided by StreamFlix are for personal, non-commercial use only.' },
          { section: 'User Conduct', content: 'You agree not to misuse the service, including unauthorized access to our recommendation engine or API.' },
          { section: 'Third-Party Content', content: 'StreamFlix uses third-party APIs (e.g., TMDB) for movie data and posters. We are not responsible for their availability or accuracy.' },
        ]);
      } finally {
        setLoading(false);
      }
    };
    fetchTerms();
  }, []);

  const retryFetch = () => {
    setLoading(true);
    setError('');
    fetchTerms();
  };

  return (
    <div className="terms">
      <main>
        <h1>Terms of Use</h1>
        {loading && <p className="loading">Loading terms...</p>}
        {error && (
          <div className="error-container">
            <p className="error">{error}</p>
            <button onClick={retryFetch} className="retry-button">Retry</button>
          </div>
        )}
        {!loading && (
          <div className="content">
            {terms.map((term, index) => (
              <div key={index} className="term-section">
                <h3>{term.section}</h3>
                <p>{term.content}</p>
              </div>
            ))}
          </div>
        )}
      </main>
      <Footer />
    </div>
  );
}

export default Terms;
