import React from 'react';
import './Home.css';

function Home({ section = 'home' }) {
  const sections = {
    home: 'Welcome to StreamFlix! Explore the best movies and TV shows.',
    'tv-shows': 'Discover our latest TV Shows collection.',
    movies: 'Check out the hottest Movies now streaming.',
    'new-popular': 'New & Popular content just for you!',
    'my-list': 'Your personalized list of favorites.',
  };

  return (
    <div className="home-container">
      <h1 className="home-title">{section.replace('-', ' ').toUpperCase()}</h1>
      <p className="home-description">{sections[section]}</p>
      {/* Add dynamic content or placeholders for each section */}
      <div className="content-placeholder">
        {section === 'home' && <p>Featured content will appear here.</p>}
        {section !== 'home' && <p>Content for {section} will be loaded dynamically.</p>}
      </div>
    </div>
  );
}

export default Home;
