import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Hero from '../components/Hero';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Home.css';

// Import posters
import kalkiPoster from '../data/Kalki.jpg';
import salaarPoster from '../data/salaar.jpg';
import chaavaPoster from '../data/chaava.jpg';
import rrrPoster from '../data/rrr.jpg';
import pushpa2Poster from '../data/pushpa2.jpg';
import hiNannaPoster from '../data/hinanna.jpg';
import mad2Poster from '../data/mad2.jpg';
import courtPoster from '../data/court.jpg';
import dragonPoster from '../data/rod.jpg';
import saripodaPoster from '../data/ss.jpg';
import saahoPoster from '../data/saaho.jpg';
import baahubaliPoster from '../data/bb2.jpg';
import blackPoster from '../data/black.jpg';

function Home() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [recommendations, setRecommendations] = useState({});
  const [initialRows, setInitialRows] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [favorites, setFavorites] = useState(() => 
    JSON.parse(localStorage.getItem('myList') || '[]')
  );
  const [notification, setNotification] = useState('');

  const staticMoviesWithLocalPosters = {
    "Chaava": chaavaPoster,
    "Mad2": mad2Poster,
    "Saripoda Sanivaaram": saripodaPoster,
  };

  const featuredItem = {
    id: 271726,
    title: "Baahubali: The Beginning",
    description: "A Telugu epic about valor and sacrifice.",
    thumbnail: baahubaliPoster,
    video: "/videos/bb2.mp4",
    language: "te"
  };

  const initialRowsData = [
    {
      title: "Trending Movies",
      items: [
        { id: 900352, title: "Kalki 2898 AD", thumbnail: kalkiPoster, description: "A sci-fi epic set in a dystopian future.", language: "te" },
        { id: 934632, title: "Salaar", thumbnail: salaarPoster, description: "An action-packed saga of power and betrayal.", language: "te" },
        { id: 0, title: "Chaava", thumbnail: chaavaPoster, description: "A historical drama about bravery.", isStatic: true, language: "te" },
        { id: 614933, title: "RRR", thumbnail: rrrPoster, description: "A revolutionary tale of friendship and rebellion.", language: "te" },
        { id: 614934, title: "Pushpa 2: The Rule", thumbnail: pushpa2Poster, description: "A gripping sequel to the smuggling saga.", language: "te" },
        { id: 1121402, title: "Hi Nanna", thumbnail: hiNannaPoster, description: "A heartwarming family drama.", language: "te" },
        { id: 1, title: "Mad2", thumbnail: mad2Poster, description: "A thrilling continuation of mystery.", isStatic: true, language: "te" },
        { id: 297282, title: "Court", thumbnail: courtPoster, description: "A thought-provoking legal drama.", language: "te" },
        { id: 1560, title: "Return of the Dragon", thumbnail: dragonPoster, description: "A classic martial arts adventure.", language: "te" },
        { id: 614932, title: "Saaho", thumbnail: saahoPoster, description: "A high-octane action thriller.", language: "te" },
        { id: 2, title: "Saripoda Sanivaaram", thumbnail: saripodaPoster, description: "An intense vigilante story.", isStatic: true, language: "te" },
      ],
    },
    {
      title: "Watch It Again",
      items: [
        { id: 5, title: "Inception", thumbnail: blackPoster, description: "A mind-bending heist movie.", language: "en" },
        { id: 6, title: "Titanic", thumbnail: blackPoster, description: "A romantic disaster film.", language: "en" },
      ],
    },
  ];

  useEffect(() => {
    window.scrollTo(0, 0);
    localStorage.setItem('myList', JSON.stringify(favorites));
    console.log('Favorites saved to localStorage:', favorites);
  }, [favorites]);

  useEffect(() => {
    sessionStorage.setItem('recommendations', JSON.stringify(recommendations));
    console.log('Recommendations saved to sessionStorage:', recommendations);
  }, [recommendations]);

  useEffect(() => {
    // Clear recommendations on page load
    setRecommendations({});
    sessionStorage.removeItem('recommendations');
    console.log('Cleared recommendations on page load');
  }, []);

  const toggleFavorite = (item) => {
    setFavorites((prevFavorites) => {
      const isFavorited = prevFavorites.some((fav) => fav.id === item.id);
      if (isFavorited) {
        console.log('Removing from favorites:', item.title);
        const updatedFavorites = prevFavorites.filter((fav) => fav.id !== item.id);
        setNotification(`"${item.title}" removed from favorites`);
        setTimeout(() => setNotification(''), 3000);
        return updatedFavorites;
      } else {
        setNotification(`"${item.title}" added to favorites`);
        setTimeout(() => setNotification(''), 3000);
        console.log('Adding to favorites:', item.title);
        return [...prevFavorites, item];
      }
    });
  };

  const fetchRandomTeluguRecommendations = async (movieTitle, numMovies) => {
    // Check if recommendations already exist in state/sessionStorage
    if (recommendations[movieTitle] && recommendations[movieTitle].length > 0) {
      console.log(`Using existing recommendations for "${movieTitle}" from sessionStorage:`, recommendations[movieTitle]);
      return recommendations[movieTitle];
    }

    try {
      console.log(`Fetching ${numMovies} random Telugu recommendations for "${movieTitle}"...`);
      const response = await fetch('http://localhost:5000/random-telugu', {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
      });
      console.log(`/random-telugu response: status ${response.status}, statusText: ${response.statusText}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}, statusText: ${response.statusText}`);
      }
      const data = await response.json();
      if (Array.isArray(data)) {
        console.log(`Received ${data.length} random Telugu recommendations for "${movieTitle}":`, data);
        // Remove duplicates based on title
        const uniqueData = Array.from(new Map(data.map(item => [item.title.toLowerCase(), item])).values());
        console.log(`After removing duplicates, ${uniqueData.length} unique Telugu recommendations for "${movieTitle}":`, uniqueData);
        // Filter for Telugu movies and valid posters
        const validData = uniqueData.filter(item => {
          const isTelugu = item.original_language === 'te';
          const isValidPoster = item.poster && typeof item.poster === 'string' && item.poster.trim() !== '' && item.poster !== 'N/A' && item.poster.match(/\.(jpg|jpeg|png|gif)$/i);
          if (!isTelugu) {
            console.warn(`Non-Telugu movie "${item.title}" filtered out: language ${item.original_language}`);
          }
          if (!isValidPoster) {
            console.warn(`Invalid poster for "${item.title}":`, item.poster);
          }
          return isTelugu && isValidPoster;
        });
        console.log(`After filtering, ${validData.length} valid Telugu recommendations for "${movieTitle}":`, validData);
        // Pick numMovies items (no shuffling for consistency)
        const selectedRecommendations = validData.slice(0, Math.min(numMovies, validData.length)).map((rec, i) => {
          console.log(`Processing recommendation ${i}:`, rec);
          const poster = rec.poster || blackPoster;
          console.log(`Poster for ${rec.title}:`, poster);
          return {
            id: i + 200,
            title: rec.title,
            thumbnail: poster,
            description: `Language: ${rec.original_language || 'te'}, Genres: ${rec.genres || 'N/A'}`,
            language: "te"
          };
        });
        // If no valid recommendations, set empty array
        if (selectedRecommendations.length === 0) {
          console.warn(`No valid Telugu recommendations for "${movieTitle}" after filtering, setting empty array`);
          setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
          return [];
        } else {
          setRecommendations((prev) => ({ ...prev, [movieTitle]: selectedRecommendations }));
          return selectedRecommendations;
        }
      } else {
        throw new Error('Invalid data format from /random-telugu endpoint');
      }
    } catch (err) {
      console.error(`Error fetching random Telugu recommendations for "${movieTitle}":`, err);
      setError(`Network error fetching random recommendations: ${err.message}`);
      setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
      return [];
    }
  };

  const fetchPosterForMovie = async (movieTitle) => {
    try {
      console.log(`Fetching poster for "${movieTitle}" via /recommend endpoint...`);
      const response = await fetch('http://localhost:5000/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: movieTitle }),
      });
      if (!response.ok) {
        console.warn(`Failed to fetch poster for "${movieTitle}": HTTP status ${response.status}, ${response.statusText}`);
        return blackPoster;
      }
      const data = await response.json();
      console.log(`Poster fetch response for "${movieTitle}":`, data);
      if (Array.isArray(data) && data.length > 0) {
        const firstMovie = data[0];
        if (firstMovie.poster && typeof firstMovie.poster === 'string' && firstMovie.poster.trim() !== '' && firstMovie.poster !== 'N/A' && firstMovie.poster.match(/\.(jpg|jpeg|png|gif)$/i)) {
          console.log(`Valid poster found for "${movieTitle}":`, firstMovie.poster);
          return firstMovie.poster;
        } else {
          console.warn(`No valid poster field in response for "${movieTitle}":`, firstMovie);
          return blackPoster;
        }
      } else {
        console.warn(`Empty or invalid response for "${movieTitle}" when fetching poster`);
        return blackPoster;
      }
    } catch (err) {
      console.error(`Error fetching poster for "${movieTitle}":`, err);
      return blackPoster;
    }
  };

  const fetchRecommendations = async (movieTitle) => {
    // Check if recommendations already exist in state/sessionStorage
    if (recommendations[movieTitle] && recommendations[movieTitle].length > 0) {
      console.log(`Using existing recommendations for "${movieTitle}" from sessionStorage:`, recommendations[movieTitle]);
      return recommendations[movieTitle];
    }

    try {
      console.log(`Fetching recommendations for "${movieTitle}"...`);
      const response = await fetch('http://localhost:5000/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: movieTitle }),
      });
      const data = await response.json();
      console.log(`Response for /recommend: status ${response.status}, data:`, data);
      if (response.ok && Array.isArray(data) && data.length > 0) {
        const uniqueData = Array.from(new Map(data.map(item => [item.title.toLowerCase(), item])).values());
        console.log(`After removing duplicates, ${uniqueData.length} unique recommendations for "${movieTitle}":`, uniqueData);
        const numRecs = 6;
        console.log(`Selecting ${numRecs} recommendations for "${movieTitle}"...`);
        const selectedRecommendations = uniqueData.slice(0, Math.min(numRecs, uniqueData.length)).map((rec, i) => {
          const poster = rec.poster && typeof rec.poster === 'string' && rec.poster.trim() !== '' && rec.poster !== 'N/A' && rec.poster.match(/\.(jpg|jpeg|png|gif)$/i) ? rec.poster : blackPoster;
          console.log(`Poster for ${rec.title}:`, poster);
          if (!rec.poster || rec.poster === 'N/A') {
            console.warn(`No valid poster for ${rec.title}, using blackPoster`);
          }
          return {
            id: i + 100,
            title: rec.title,
            thumbnail: poster,
            description: `Language: ${rec.original_language || 'en'}, Genres: ${rec.genres || 'N/A'}`,
            language: rec.original_language || 'en'
          };
        });
        setRecommendations((prev) => ({ ...prev, [movieTitle]: selectedRecommendations }));
        return selectedRecommendations;
      } else {
        console.log(`No valid data from /recommend for "${movieTitle}"`);
        setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
        return [];
      }
    } catch (err) {
      console.error(`Network error for "${movieTitle}":`, err);
      setError(`Network error fetching recommendations: ${err.message}`);
      setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
      return [];
    }
  };

  useEffect(() => {
    const fetchInitialData = async () => {
      setLoading(true);
      try {
        const updatedRows = JSON.parse(JSON.stringify(initialRowsData));
        const watchItAgainRow = updatedRows.find(row => row.title === "Watch It Again");
        if (watchItAgainRow) {
          for (const item of watchItAgainRow.items) {
            if (item.language === "en") {
              const poster = await fetchPosterForMovie(item.title);
              item.thumbnail = poster;
            }
          }
        }
        let baahubaliRecommendations = [];
        if (featuredItem.language === "te") {
          baahubaliRecommendations = await fetchRandomTeluguRecommendations(featuredItem.title, 6);
        }
        setInitialRows(updatedRows);
        if (baahubaliRecommendations.length > 0) {
          setRecommendations((prev) => ({ ...prev, [featuredItem.title]: baahubaliRecommendations }));
        }
      } catch (err) {
        setError('Failed to fetch initial data');
        console.error('Initial fetch error:', err);
      } finally {
        setLoading(false);
      }
    };
    fetchInitialData();
  }, [featuredItem.language, featuredItem.title, fetchRandomTeluguRecommendations, initialRowsData]);

  const handleMoreInfo = (item) => {
    const updatedItem = { ...item };
    if (item.isStatic && staticMoviesWithLocalPosters[item.title]) {
      updatedItem.thumbnail = staticMoviesWithLocalPosters[item.title];
    }
    setSelectedItem(updatedItem);
    // Only fetch if recommendations don't exist
    if (!recommendations[item.title] || recommendations[item.title].length === 0) {
      if (item.language === "te") {
        fetchRandomTeluguRecommendations(item.title, 6);
      } else {
        fetchRecommendations(item.title);
      }
    } else {
      console.log(`Skipping fetch for "${item.title}", using existing recommendations:`, recommendations[item.title]);
    }
  };

  return (
    <div className="home">
      <Header />
      <Hero
        item={featuredItem}
        onMoreInfo={handleMoreInfo}
        favorites={favorites || []}
        toggleFavorite={toggleFavorite}
      />
      {loading ? (
        <div className="loading">Loading...</div>
      ) : (
        <>
          {(initialRows.length > 0 ? initialRows : initialRowsData).map((row, index) => (
            <Row
              key={index}
              title={row.title}
              items={row.items}
              onMoreInfo={handleMoreInfo}
              favorites={favorites || []}
              toggleFavorite={toggleFavorite}
            />
          ))}
          {Object.entries(recommendations).map(([title, recs]) => (
            <Row
              key={`rec-${title}`}
              title={`Recommendations for ${title}`}
              items={recs}
              onMoreInfo={handleMoreInfo}
              favorites={favorites || []}
              toggleFavorite={toggleFavorite}
            />
          ))}
          {!Object.keys(recommendations).length && (
            <div className="no-recommendations">No recommendations loaded yet.</div>
          )}
          {error && <div className="error">{error}</div>}
        </>
      )}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      {notification && <div className="notification">{notification}</div>}
      <Footer />
    </div>
  );
}

export default Home;
