import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Footer from '../components/Footer';
import './Help.css';

function Help() {
  const [searchQuery, setSearchQuery] = useState('');
  const [faqs, setFaqs] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    window.scrollTo(0, 0);
    const fetchFaqs = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/faqs');
        setFaqs(response.data);
      } catch (err) {
        setError('Failed to load FAQs.');
        setFaqs([
          { id: 1, question: 'How do I reset my password?', answer: 'Go to Profile > Settings.' },
          { id: 2, question: 'Why is my video buffering?', answer: 'Check your internet speed.' },
        ]);
      }
    };
    fetchFaqs();
  }, []);

  const filteredFaqs = faqs.filter((faq) =>
    faq.question.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="help">
      <Header />
      <main>
        <h1>Help Center</h1>
        <p>Search for answers or browse FAQs.</p>
        <div className="search-bar">
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search help topics..."
          />
        </div>
        {error && <p className="error">{error}</p>}
        <div className="faqs">
          {filteredFaqs.length > 0 ? (
            filteredFaqs.map((faq) => (
              <div key={faq.id} className="faq">
                <h3>{faq.question}</h3>
                <p>{faq.answer}</p>
              </div>
            ))
          ) : (
            <p>No results found.</p>
          )}
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default Help;
