import React, { useState, useEffect, useRef } from 'react';
import './Hero.css';

function Hero({ item, onMoreInfo }) {
  const [showVideo, setShowVideo] = useState(false);
  const [videoError, setVideoError] = useState(false);
  const [showAudioPrompt, setShowAudioPrompt] = useState(true);
  const videoRef = useRef(null);

  useEffect(() => {
    console.log('Hero item:', item);
    if (!item || !item.video) {
      console.warn('No video provided for item:', item);
      setVideoError(true);
      return;
    }
    console.log('Starting 3-second delay for video:', item.video);
    const timer = setTimeout(() => {
      console.log(`Attempting to play video for "${item.title}"`);
      setShowVideo(true);
    }, 3000);
    return () => {
      console.log('Clearing timer');
      clearTimeout(timer);
    };
  }, [item]);

  useEffect(() => {
    if (showVideo && videoRef.current && !videoError) {
      console.log('Video element ready, attempting play with sound');
      videoRef.current.play().catch((err) => {
        console.error('Video playback failed:', err.message);
        setShowAudioPrompt(true);
        setVideoError(true);
      });
    }
  }, [showVideo, videoError]);

  const handleInteraction = () => {
    if (videoRef.current && (videoError || videoRef.current.muted)) {
      console.log('User interacted, retrying playback with sound');
      videoRef.current.muted = false;
      videoRef.current.play().then(() => {
        console.log('Video playing with sound');
        setShowAudioPrompt(false);
        setVideoError(false);
      }).catch((err) => {
        console.error('Retry playback failed:', err.message);
        setVideoError(true);
      });
    }
  };

  if (!item) return null;

  return (
    <div
      className="hero"
      style={{
        backgroundImage: !showVideo || videoError ? `url(${item.thumbnail})` : 'none',
      }}
      onClick={handleInteraction}
    >
      {showVideo && item.video && !videoError ? (
        <div className="video-container">
          <video
            ref={videoRef}
            src={item.video}
            autoPlay
            muted={false}
            loop
            playsInline
            className="hero-video"
            onError={(e) => {
              console.error(`Video error for ${item.title}:`, e.target.error);
              console.error('Error code:', e.target.error.code);
              console.error('Error message:', e.target.error.message);
              setVideoError(true);
              setShowAudioPrompt(true);
            }}
            onCanPlay={() => console.log('Video can play')}
            onPlay={() => {
              console.log('Video is playing');
              setShowAudioPrompt(false);
            }}
          />
          {showAudioPrompt && (
            <div className="audio-prompt">
              <p>Click anywhere to enable sound</p>
            </div>
          )}
        </div>
      ) : showVideo && (videoError || !item.video) ? (
        <div className="video-fallback">
          <img src={item.thumbnail} alt="Video failed" />
          <p>Video failed to load. Please check the file path or try again.</p>
        </div>
      ) : null}
      <div className="hero-content">
        <h1 className="hero-title">{item.title}</h1>
        <p className="hero-description">{item.description}</p>
        <div className="hero-buttons">
          <button className="btn info-btn" onClick={() => onMoreInfo(item)}>
            <i className="fas fa-info-circle" /> More Info
          </button>
        </div>
      </div>
    </div>
  );
}

export default Hero;
