import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './MyList.css';

function MyList() {
  const [myList, setMyList] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);

  // Sync myList with localStorage on mount and whenever it changes
  useEffect(() => {
    const savedList = JSON.parse(localStorage.getItem('myList') || '[]');
    console.log('MyList synced:', savedList); // Debug log
    setMyList(savedList);

    // Listen for storage changes (e.g., from other tabs)
    const handleStorageChange = (e) => {
      if (e.key === 'myList') {
        setMyList(JSON.parse(e.newValue || '[]'));
      }
    };
    window.addEventListener('storage', handleStorageChange);

    // Cleanup event listener
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  // Update localStorage whenever myList changes
  useEffect(() => {
    localStorage.setItem('myList', JSON.stringify(myList));
  }, [myList]);

  const handleMoreInfo = (item) => {
    setSelectedItem(item);
  };

  const toggleFavorite = (item) => {
    // Only remove if already in the list; do nothing if not (to prevent accidental adds)
    const isFavorited = myList.some((fav) => fav.id === item.id);
    if (isFavorited) {
      const updatedList = myList.filter((fav) => fav.id !== item.id);
      setMyList(updatedList);
      localStorage.setItem('myList', JSON.stringify(updatedList));
      console.log('Removed from MyList:', item.title);
    } else {
      console.log('Item not in MyList, no action taken:', item.title);
    }
  };

  return (
    <div className="my-list">
      {/* <Header /> */}
      <h1>My List</h1>
      {myList.length > 0 ? (
        <Row
          title="Your Saved Movies"
          items={myList}
          onMoreInfo={handleMoreInfo}
          favorites={myList}
          toggleFavorite={toggleFavorite}
        />
      ) : (
        <p className="empty-list">Your list is empty. Start adding movies!</p>
      )}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      <Footer />
    </div>
  );
}

export default MyList;
