import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Footer from '../components/Footer';
import './Help.css';

function Help() {
  const [searchQuery, setSearchQuery] = useState('');
  const [faqs, setFaqs] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    window.scrollTo(0, 0);
    const fetchFaqs = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/faqs');
        setFaqs(response.data);
      } catch (err) {
        setError('Failed to load FAQs.');
        const fallbackFaqs = Array.from({ length: 50 }, (_, i) => ({
          id: i + 1,
          question: `Sample Question ${i + 1}?`,
          answer: `This is a detailed answer for sample question number ${i + 1}. Here we explain how the feature works, what to do in case of issues, and any related tips or instructions.`
        }));
        setFaqs(fallbackFaqs);
      }
    };
    fetchFaqs();
  }, []);

  const filteredFaqs = faqs.filter((faq) => {
    if (!searchQuery.trim()) return true;
    const query = searchQuery.toLowerCase();
    return (
      faq.question.toLowerCase().includes(query) ||
      faq.answer.toLowerCase().includes(query)
    );
  });

  return (
    <div className="help">
      <main className="help-container">
        <h1>Help Center</h1>
        <p>Search for answers or browse FAQs below.</p>

        <div className="search-bar">
          <input
            type="text"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            placeholder="Search help topics..."
          />
        </div>

        {error && <p className="error">{error}</p>}

        <div className="faqs-scroll-container">
          {filteredFaqs.length > 0 ? (
            filteredFaqs.map((faq) => (
              <div key={faq.id} className="faq">
                <h3>{faq.question}</h3>
                <p>{faq.answer}</p>
              </div>
            ))
          ) : (
            <p>No results found.</p>
          )}
        </div>
      </main>

      <Footer />
    </div>
  );
}

export default Help;
