import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Hero from '../components/Hero';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Home.css';

import kalkiPoster from '../data/Kalki.jpg';
import salaarPoster from '../data/salaar.jpg';
import chaavaPoster from '../data/chaava.jpg';
import rrrPoster from '../data/rrr.jpg';
import pushpa2Poster from '../data/pushpa2.jpg';
import hiNannaPoster from '../data/hinanna.jpg';
import mad2Poster from '../data/mad2.jpg';
import courtPoster from '../data/court.jpg';
import dragonPoster from '../data/rod.jpg';
import saripodaPoster from '../data/ss.jpg';
import saahoPoster from '../data/saaho.jpg';
import baahubaliPoster from '../data/bb2.jpg';
import blackPoster from '../data/black.jpg';

function Home() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [recommendations, setRecommendations] = useState({});
  const [initialRows, setInitialRows] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [favorites, setFavorites] = useState(() => JSON.parse(localStorage.getItem('myList') || '[]'));

  const staticMoviesWithLocalPosters = {
    Chaava: chaavaPoster,
    Mad2: mad2Poster,
    'Saripodhaa Sanivaaram': saripodaPoster,
    Saaho: saahoPoster,
    'Baahubali 2: The Conclusion': baahubaliPoster
  };

  const fallbackTeluguMovies = [
    { id: 271726, title: 'Baahubali: The Beginning', thumbnail: baahubaliPoster, description: 'Language: te, Genres: Action, Drama, Year: 2015', language: 'te', genres: ['Action', 'Drama'], release_year: 2015 },
    { id: 301345, title: 'Baahubali 2: The Conclusion', thumbnail: baahubaliPoster, description: 'Language: te, Genres: Action, Drama, Year: 2017', language: 'te', genres: ['Action', 'Drama'], release_year: 2017 },
    { id: 134374, title: '1 - Nenokkadine', thumbnail: blackPoster, description: 'Language: te, Genres: Action, Thriller, Year: 2014', language: 'te', genres: ['Action', 'Thriller'], release_year: 2014 },
    { id: 44977, title: 'Dhoom:3', thumbnail: blackPoster, description: 'Language: te, Genres: Action, Thriller, Year: 2013', language: 'te', genres: ['Action', 'Thriller'], release_year: 2013 }
  ];

  const featuredItem = {
    id: 271726,
    title: 'Baahubali: The Beginning',
    description: 'A Telugu epic about valor and sacrifice.',
    thumbnail: baahubaliPoster,
    language: 'te',
    genres: ['Action', 'Drama'],
    release_year: 2015
  };

  const initialRowsData = [
    {
      title: 'Trending Movies',
      items: [
        { id: 900352, title: 'Kalki 2898 AD', thumbnail: kalkiPoster, description: 'Language: te, Genres: Sci-Fi, Mythology, Action', language: 'te', isStatic: true, genres: ['Sci-Fi', 'Mythology', 'Action'], release_year: 'N/A' },
        { id: 934632, title: 'Salaar: Part 1 â€“ Ceasefire', thumbnail: salaarPoster, description: 'Language: te, Genres: Action, Drama, Epic', language: 'te', isStatic: true, genres: ['Action', 'Drama', 'Epic'], release_year: 'N/A' },
        { id: 0, title: 'Chhaava', thumbnail: chaavaPoster, description: 'Language: hi, Genres: Historical, Action, Drama', language: 'hi', isStatic: true, genres: ['Historical', 'Action', 'Drama'], release_year: 'N/A' },
        { id: 614933, title: 'RRR', thumbnail: rrrPoster, description: 'Language: te, Genres: Action, Historical, Drama, Epic', language: 'te', isStatic: true, genres: ['Action', 'Historical', 'Drama', 'Epic'], release_year: 'N/A' },
        { id: 614934, title: 'Pushpa 2: The Rule', thumbnail: pushpa2Poster, description: 'Language: te, Genres: Action, Drama', language: 'te', isStatic: true, genres: ['Action', 'Drama'], release_year: 'N/A' },
        { id: 1121402, title: 'Hi Nanna', thumbnail: hiNannaPoster, description: 'Language: te, Genres: Romance, Drama', language: 'te', isStatic: true, genres: ['Romance', 'Drama'], release_year: 'N/A' },
        { id: 1, title: 'Mad2', thumbnail: mad2Poster, description: 'Language: te, Genres: Comedy, Drama, Coming-of-Age', language: 'te', isStatic: true, genres: ['Comedy', 'Drama', 'Coming-of-Age'], release_year: 'N/A' },
        { id: 297282, title: 'Court', thumbnail: courtPoster, description: 'Language: mr, Genres: Drama, Legal', language: 'mr', isStatic: true, genres: ['Drama', 'Legal'], release_year: 'N/A' },
        { id: 1560, title: 'Return of the Dragon', thumbnail: dragonPoster, description: 'Language: en, Genres: Action, Comedy, Martial Arts', language: 'en', isStatic: true, genres: ['Action', 'Comedy', 'Martial Arts'], release_year: 'N/A' },
        { id: 614932, title: 'Saaho', thumbnail: saahoPoster, description: 'Language: te, Genres: Action, Thriller', language: 'te', isStatic: true, genres: ['Action', 'Thriller'], release_year: 'N/A' },
        { id: 2, title: 'Saripodhaa Sanivaaram', thumbnail: saripodaPoster, description: 'Language: te, Genres: Action, Thriller, Vigilante', language: 'te', isStatic: true, genres: ['Action', 'Thriller', 'Vigilante'], release_year: 'N/A' },
        { id: 301345, title: 'Baahubali 2: The Conclusion', thumbnail: baahubaliPoster, description: 'Language: te, Genres: Action, Drama', language: 'te', isStatic: true, genres: ['Action', 'Drama'], release_year: 2017 }
      ]
    },
    {
      title: 'Watch It Again',
      items: [
        { id: 5, title: 'Inception', thumbnail: blackPoster, description: 'Language: en, Genres: Sci-Fi, Thriller', language: 'en', genres: ['Sci-Fi', 'Thriller'], release_year: 'N/A' },
        { id: 6, title: 'Titanic', thumbnail: blackPoster, description: 'Language: en, Genres: Romance, Drama', language: 'en', genres: ['Romance', 'Drama'], release_year: 'N/A' }
      ]
    }
  ];

  const staticMovieTitles = initialRowsData[0].items.map(item => item.title);

  useEffect(() => {
    localStorage.setItem('myList', JSON.stringify(favorites));
  }, [favorites]);

  const toggleFavorite = (item) => {
    setFavorites((prev) => {
      const isFavorited = prev.some((fav) => fav.id === item.id);
      return isFavorited ? prev.filter((fav) => fav.id !== item.id) : [...prev, item];
    });
  };

  const fetchTeluguRecommendationsByGenre = async (movieTitle, genres, numMovies) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:5000/random-telugu-by-genre', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ genres, exclude_titles: staticMovieTitles })
      });
      if (!response.ok) {
        throw new Error(`HTTP error: ${await response.text()}`);
      }
      const data = await response.json();
      if (Array.isArray(data)) {
        const recs = data.slice(0, numMovies).map((rec, i) => ({
          id: rec.id || (i + 200),
          title: rec.title,
          thumbnail: rec.poster || blackPoster,
          description: `Language: ${rec.original_language || 'te'}, Genres: ${rec.genres.join(', ') || 'N/A'}, Year: ${rec.release_year || 'N/A'}`,
          language: rec.original_language || 'te',
          genres: rec.genres || [],
          release_year: rec.release_year || 'N/A'
        }));
        setRecommendations((prev) => ({ ...prev, [movieTitle]: recs }));
      } else {
        throw new Error('Invalid data format');
      }
    } catch (err) {
      setError(`Failed to fetch recommendations for "${movieTitle}": ${err.message}`);
      setRecommendations((prev) => ({ ...prev, [movieTitle]: fallbackTeluguMovies.slice(0, numMovies) }));
    } finally {
      setLoading(false);
    }
  };

  const fetchRecommendations = async (movieTitle) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:5000/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: movieTitle })
      });
      if (!response.ok) {
        throw new Error(`HTTP error: ${await response.text()}`);
      }
      const data = await response.json();
      if (Array.isArray(data)) {
        const recs = data.slice(0, 5).map((rec, i) => ({
          id: rec.movie_id || rec.id || (i + 100),
          title: rec.title,
          thumbnail: rec.poster || blackPoster,
          description: `Language: ${rec.original_language || 'en'}, Genres: ${rec.genres.join(', ') || 'N/A'}, Year: ${rec.release_year || 'N/A'}`,
          language: rec.original_language || 'en',
          genres: rec.genres || [],
          release_year: rec.release_year || 'N/A'
        }));
        setRecommendations((prev) => ({ ...prev, [movieTitle]: recs }));
      } else {
        throw new Error('Invalid data format');
      }
    } catch (err) {
      setError(`Failed to fetch recommendations for "${movieTitle}": ${err.message}`);
      setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const fetchInitialData = async () => {
      setLoading(true);
      try {
        setInitialRows(initialRowsData);
        if (featuredItem.language === 'te' && !recommendations[featuredItem.title]) {
          await fetchTeluguRecommendationsByGenre(featuredItem.title, featuredItem.genres, 5);
        }
      } catch (err) {
        setError('Failed to fetch initial data: ' + err.message);
      } finally {
        setLoading(false);
      }
    };
    fetchInitialData();
  }, [recommendations]);

  const handleMoreInfo = (item) => {
    setSelectedItem({ ...item, thumbnail: item.isStatic && staticMoviesWithLocalPosters[item.title] ? staticMoviesWithLocalPosters[item.title] : item.thumbnail });
    if (staticMovieTitles.includes(item.title) || item.language === 'te') {
      fetchTeluguRecommendationsByGenre(item.title, item.genres, 5);
    } else {
      fetchRecommendations(item.title);
    }
  };

  return (
    <div className="home">
      <Header />
      <Hero item={featuredItem} onMoreInfo={handleMoreInfo} favorites={favorites} toggleFavorite={toggleFavorite} />
      {initialRows.map((row, index) => (
        <Row key={index} title={row.title} items={row.items} onMoreInfo={handleMoreInfo} favorites={favorites} toggleFavorite={toggleFavorite} />
      ))}
      {Object.entries(recommendations).map(([title, recs]) => (
        <Row key={`rec-${title}`} title={`Recommendations for ${title}`} items={recs} onMoreInfo={handleMoreInfo} favorites={favorites} toggleFavorite={toggleFavorite} />
      ))}
      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error">{error}</div>}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      <Footer />
    </div>
  );
}

export default Home;
