import React, { useState } from 'react';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Search.css';

function Search() {
  const [searchTitle, setSearchTitle] = useState('');
  const [results, setResults] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    if (!searchTitle.trim()) {
      setError('Please enter a movie title');
      setResults([]);
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await fetch('http://localhost:5000/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: searchTitle }),
      });
      const data = await response.json();

      if (response.ok && Array.isArray(data)) {
        const mappedResults = data.map((rec, i) => ({
          id: i + 100, // Backend may not provide unique IDs
          title: rec.title || 'Unknown Title',
          thumbnail: rec.poster || 'https://via.placeholder.com/200x300?text=Poster',
          description: `Language: ${rec.original_language || 'N/A'}, Genres: ${rec.genres || 'N/A'}`,
        }));
        setResults(mappedResults);
      } else {
        setError(data.error || 'No results found');
        setResults([]);
      }
    } catch (err) {
      setError('Network error while searching');
      console.error('Search error:', err);
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  const handleMoreInfo = (item) => {
    setSelectedItem(item);
  };

  return (
    <div className="search">
      <div className="search-container">
        <div className="search-bar">
          <input
            type="text"
            value={searchTitle}
            onChange={(e) => setSearchTitle(e.target.value)}
            placeholder="Search for a movie"
          />
          <button onClick={handleSearch}>Search</button>
        </div>
        {loading && <div className="loading">Loading...</div>}
        {error && <div className="error">{error}</div>}
        {results.length > 0 && (
          <Row
            title={`Search Results for "${searchTitle}"`}
            items={results}
            onMoreInfo={handleMoreInfo}
          />
        )}
        {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      </div>
      <Footer />
    </div>
  );
}

export default Search;
