import React, { useState, useEffect } from 'react';
import Footer from '../components/Footer';
import './CorporateInfo.css';

function CorporateInfo() {
  const [info, setInfo] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    window.scrollTo(0, 0);
    const fetchInfo = async () => {
      try {
        // Simulate fetch failure and load static data
        throw new Error('Static fallback');
      } catch (err) {
        setError('Failed to load info.');
        setInfo({
          about: 'StreamFlix, founded in 2020, is a global streaming service offering a vast collection of entertainment across genres, including films, TV shows, documentaries, and originals.',
          headquarters: '123 Stream Way, Los Angeles, CA 90001, United States',
          contact: 'Email: info@streamflix.com | Phone: +1-800-555-STREAM',
          mission: 'To deliver world-class entertainment that brings people together and celebrates storytelling across cultures.',
          leadership: 'Led by CEO Jane Doe, our executive team brings expertise from media, technology, and global markets to drive innovation and growth.',
          globalPresence: 'Available in over 190 countries, StreamFlix offers localized content and language options tailored to each region.',
          history: 'StreamFlix began as a DVD rental company before transitioning into streaming in 2020. Within five years, it became one of the top platforms globally.',
          values: 'Our core values include Innovation, Inclusion, Integrity, and Impact. We believe in creating a positive cultural and environmental footprint.',
          partnerships: 'StreamFlix collaborates with over 300 global production houses and tech innovators to enhance content and viewer experience.',
        });
      }
    };
    fetchInfo();
  }, []);

  return (
    <div className="corporate-info">
      <main>
        <h1>Corporate Information</h1>
        {error && <p className="error">{error}</p>}
        {info && (
          <div className="info-container">
            <section id="about">
              <h3>About StreamFlix</h3>
              <p>{info.about}</p>
            </section>
            <section id="headquarters">
              <h3>Headquarters</h3>
              <p>{info.headquarters}</p>
            </section>
            <section id="contact">
              <h3>Contact</h3>
              <p>{info.contact}</p>
            </section>
            <section id="mission">
              <h3>Our Mission</h3>
              <p>{info.mission}</p>
            </section>
            <section id="leadership">
              <h3>Leadership</h3>
              <p>{info.leadership}</p>
            </section>
            <section id="global-presence">
              <h3>Global Presence</h3>
              <p>{info.globalPresence}</p>
            </section>
            <section id="history">
              <h3>Our History</h3>
              <p>{info.history}</p>
            </section>
            <section id="values">
              <h3>Core Values</h3>
              <p>{info.values}</p>
            </section>
            <section id="partnerships">
              <h3>Strategic Partnerships</h3>
              <p>{info.partnerships}</p>
            </section>
          </div>
        )}
      </main>
      <Footer />
    </div>
  );
}

export default CorporateInfo;
