import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Hero from '../components/Hero';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Home.css';

function Home() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [recommendations, setRecommendations] = useState({});
  const [initialRows, setInitialRows] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const featuredItem = {
    id: 271726, // TMDB ID for Baahubali: The Beginning
    title: "Baahubali: The Beginning",
    description: "A Telugu epic about valor and sacrifice.",
    thumbnail: "http://localhost:5001/data/bb2.jpg",
    video: "http://localhost:5001/data/bb2.mp4"
  };

  const initialRowsData = [
    {
      title: "Trending Movies",
      items: [
        { id: 900352, title: "Kalki 2898 AD", thumbnail: "http://localhost:5001/data/Kalki.jpg", description: "A sci-fi epic set in a dystopian future." },
        { id: 934632, title: "Salaar", thumbnail: "http://localhost:5001/data/salaar.jpg", description: "An action-packed saga of power and betrayal." },
        { id: 0, title: "Chaava", thumbnail: "http://localhost:5001/data/chaava.jpg", description: "A historical drama about bravery." },
        { id: 614933, title: "RRR", thumbnail: "http://localhost:5001/data/rrr.jpg", description: "A revolutionary tale of friendship and rebellion." },
        { id: 614934, title: "Pushpa 2: The Rule", thumbnail: "http://localhost:5001/data/pushpa2.jpg", description: "A gripping sequel to the smuggling saga." },
        { id: 1121402, title: "Hi Nanna", thumbnail: "http://localhost:5001/data/hinanna.jpg", description: "A heartwarming family drama." },
        { id: 1, title: "Mad2", thumbnail: "http://localhost:5001/data/mad2.jpg", description: "A thrilling continuation of mystery." },
        { id: 297282, title: "Court", thumbnail: "http://localhost:5001/data/court.jpg", description: "A thought-provoking legal drama." },
        { id: 1560, title: "Return of the Dragon", thumbnail: "http://localhost:5001/data/rod.jpg", description: "A classic martial arts adventure." },
        { id: 2, title: "Saripoda Sanivaaram", thumbnail: "http://localhost:5001/data/ss.jpg", description: "An intense vigilante story." },
        { id: 614932, title: "Saaho", thumbnail: "http://localhost:5001/data/saaho.jpg", description: "A high-octane action thriller." },
      ],
    },
    {
      title: "Watch It Again",
      items: [
        { id: 5, title: "Inception", thumbnail: null, description: "A mind-bending heist movie." },
        { id: 6, title: "Titanic", thumbnail: null, description: "A romantic disaster film." },
      ],
    },
  ];

  useEffect(() => {
    const fetchInitialData = async () => {
      setLoading(true);
      try {
        const updatedRows = await Promise.all(
          initialRowsData.map(async (row) => {
            const updatedItems = await Promise.all(
              row.items.map(async (item) => {
                if (item.thumbnail) return item;
                try {
                  const response = await fetch('http://localhost:5001/recommend', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ movie_title: item.title }),
                  });
                  const data = await response.json();
                  if (response.ok && data.length > 0 && data[0].poster) {
                    return { ...item, thumbnail: data[0].poster };
                  }
                  return { ...item, thumbnail: "https://via.placeholder.com/200x300" };
                } catch (err) {
                  console.error(`Failed to fetch poster for ${item.title}:`, err);
                  return { ...item, thumbnail: "https://via.placeholder.com/200x300" };
                }
              })
            );
            return { ...row, items: updatedItems };
          })
        );
        setInitialRows(updatedRows);

        console.log('Fetching Baahubali recommendations...');
        await fetchRecommendations("Baahubali: The Beginning");
      } catch (err) {
        setError('Failed to fetch initial data');
        console.error('Initial fetch error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchInitialData();
  }, []);

  const fetchRecommendations = async (movieTitle) => {
    console.log(`Fetching recommendations for "${movieTitle}"...`);
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:5001/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: movieTitle }),
      });
      const data = await response.json();
      console.log(`Raw response for "${movieTitle}":`, data);

      if (response.ok && Array.isArray(data)) {
        const mappedRecommendations = data.map((rec, i) => ({
          id: i + 100,
          title: rec.title,
          thumbnail: rec.poster || "https://via.placeholder.com/200x300",
          description: `Language: ${rec.original_language || 'N/A'}, Genres: ${rec.genres || 'N/A'}`,
        }));
        setRecommendations((prev) => {
          const updated = { ...prev, [movieTitle]: mappedRecommendations };
          console.log(`Set recommendations for "${movieTitle}":`, mappedRecommendations);
          return updated;
        });
      } else {
        const errorMsg = typeof data === 'string' ? data : data.error || 'No recommendations available';
        setError(errorMsg);
        console.error(`Error for "${movieTitle}":`, errorMsg);
        setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
      }
    } catch (err) {
      const errorMsg = 'Network error while fetching recommendations';
      setError(errorMsg);
      console.error(`Network error for "${movieTitle}":`, err);
      setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
    } finally {
      setLoading(false);
    }
  };

  const handleMoreInfo = (item) => {
    console.log(`More Info clicked for "${item.title}"`);
    setSelectedItem({
      ...item,
      thumbnail: item.title === "Baahubali: The Beginning" 
        ? "http://localhost:5001/data/bb2.jpg" 
        : item.thumbnail
    });
    fetchRecommendations(item.title);
  };

  return (
    <div className="home">
      <Header />
      <Hero item={featuredItem} onMoreInfo={handleMoreInfo} />
      {initialRows.length > 0 ? initialRows.map((row, index) => (
        <Row key={index} title={row.title} items={row.items} onMoreInfo={handleMoreInfo} />
      )) : initialRowsData.map((row, index) => (
        <Row key={index} title={row.title} items={row.items} onMoreInfo={handleMoreInfo} />
      ))}
      {Object.entries(recommendations).map(([title, recs]) => (
        <Row
          key={`rec-${title}`}
          title={`Recommendations for ${title}`}
          items={recs}
          onMoreInfo={handleMoreInfo}
        />
      ))}
      {Object.keys(recommendations).length === 0 && !loading && (
        <div className="no-recommendations">No recommendations loaded yet.</div>
      )}
      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error">{error}</div>}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      <Footer />
    </div>
  );
}

export default Home;
