import React from 'react';
import Footer from '../components/Footer';
import './Notifications.css';

function Notifications() {
  // Sample notification data with relevant TMDB and tech images
  const notifications = [
    {
      id: 1,
      title: 'New Release: "The Cosmic Voyage"',
      message: 'Check out the new sci-fi blockbuster now streaming!',
      date: '2025-04-27',
      image: 'https://resizing.flixster.com/d8i76U3xlMKhrgoPWh0qRzfb0mU=/206x305/v2/https://resizing.flixster.com/-XZAfHZM39UwaGJIFWKAE8fS0ak=/v3/t/assets/p85466_v_v10_aa.jpg', // Interstellar (sci-fi)
    },
    {
      id: 2,
      title: 'Recommendation: "Mystery Manor"',
      message: 'We think you’ll love this thrilling mystery series.',
      date: '2025-04-26',
      image: 'https://image.tmdb.org/t/p/w92/rweIrveL67O5YxHjouXY3SJo4CF.jpg', // Sherlock (mystery)
    },
    {
      id: 3,
      title: 'Speed Test Reminder',
      message: 'Test your connection to ensure smooth streaming.',
      date: '2025-04-25',
      image: 'https://images.unsplash.com/photo-1594908900066-3f47337549d8?crop=entropy&cs=tinysrgb&fit=crop&fm=jpg&h=50&w=50', // Speedometer
    },
  ];

  return (
    <div className="notifications">
      <main className="notifications-container">
        <h1>Notifications</h1>
        {notifications.length === 0 ? (
          <p className="no-notifications">No new notifications.</p>
        ) : (
          <ul className="notifications-list">
            {notifications.map((notification) => (
              <li key={notification.id} className="notification-card">
                <img
                  src={notification.image}
                  alt={notification.title}
                  className="notification-image"
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/50?text=Image'; // Fallback
                  }}
                />
                <div className="notification-content">
                  <h3>{notification.title}</h3>
                  <p>{notification.message}</p>
                  <span className="notification-date">{notification.date}</span>
                </div>
              </li>
            ))}
          </ul>
        )}
      </main>
      <Footer />
    </div>
  );
}

export default Notifications;
