import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Hero from '../components/Hero';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Home.css';

// Local posters (case-sensitive)
import kalkiPoster from '../data/Kalki.jpg';
import salaarPoster from '../data/salaar.jpg';
import chaavaPoster from '../data/chaava.jpg';
import rrrPoster from '../data/rrr.jpg';
import pushpa2Poster from '../data/pushpa2.jpg';
import hiNannaPoster from '../data/hinanna.jpg';
import mad2Poster from '../data/mad2.jpg';
import courtPoster from '../data/court.jpg';
import dragonPoster from '../data/rod.jpg';
import saripodaPoster from '../data/ss.jpg';
import saahoPoster from '../data/saaho.jpg';
import baahubaliPoster from '../data/bb2.jpg';
import blackPoster from '../data/black.jpg';

// In-memory poster cache
const posterCache = new Map();
const placeholderPoster = 'https://via.placeholder.com/200x300?text=Poster';

function Home() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [recommendations, setRecommendations] = useState({});
  const [initialRows, setInitialRows] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [favorites, setFavorites] = useState(() => JSON.parse(localStorage.getItem('myList') || '[]'));

  const staticMoviesWithLocalPosters = {
    'Chaava': chaavaPoster,
    'Mad2': mad2Poster,
    'Saripodhaa Sanivaaram': saripodaPoster,
    'Saaho': saahoPoster,
    'Baahubali 2: The Conclusion': baahubaliPoster,
    'Kalki 2898 AD': kalkiPoster,
    'Salaar: Part 1 – Ceasefire': salaarPoster,
    'RRR': rrrPoster,
    'Pushpa 2: The Rule': pushpa2Poster,
    'Hi Nanna': hiNannaPoster,
    'Court': courtPoster,
    'Return of the Dragon': dragonPoster
  };

  const featuredItem = {
    id: 301344,
    title: 'Baahubali: The Beginning',
    description: 'A Telugu epic about valor and sacrifice.',
    thumbnail: 'https://image.tmdb.org/t/p/w500/9gm3lL8n3dZA9EKGTHZdt16qMKD.jpg',
    language: 'te',
    videoUrl: 'https://www.youtube.com/embed/sOEg_YZQsTI' // Sample trailer
  };

  const initialRowsData = [
    {
      title: 'Trending Movies',
      items: [
        { id: 900352, title: 'Kalki 2898 AD', thumbnail: kalkiPoster, description: 'Futuristic mythology.', language: 'te', isStatic: true },
        { id: 934632, title: 'Salaar: Part 1 – Ceasefire', thumbnail: salaarPoster, description: 'Action saga.', language: 'te', isStatic: true },
        { id: 0, title: 'Chaava', thumbnail: chaavaPoster, description: 'Historical drama.', language: 'hi', isStatic: true },
        { id: 614933, title: 'RRR', thumbnail: rrrPoster, description: 'Revolutionary tale.', language: 'te', isStatic: true },
        { id: 614934, title: 'Pushpa 2: The Rule', thumbnail: pushpa2Poster, description: 'Smuggling sequel.', language: 'te', isStatic: true },
        { id: 1121402, title: 'Hi Nanna', thumbnail: hiNannaPoster, description: 'Family drama.', language: 'te', isStatic: true },
        { id: 1, title: 'Mad2', thumbnail: mad2Poster, description: 'Thrilling mystery.', language: 'te', isStatic: true },
        { id: 297282, title: 'Court', thumbnail: courtPoster, description: 'Legal drama.', language: 'mr', isStatic: true },
        { id: 1560, title: 'Return of the Dragon', thumbnail: dragonPoster, description: 'Martial arts.', language: 'en', isStatic: true },
        { id: 614932, title: 'Saaho', thumbnail: saahoPoster, description: 'Action thriller.', language: 'te', isStatic: true },
        { id: 2, title: 'Saripodhaa Sanivaaram', thumbnail: saripodaPoster, description: 'Vigilante story.', language: 'te', isStatic: true },
        { id: 301345, title: 'Baahubali 2: The Conclusion', thumbnail: baahubaliPoster, description: 'Epic conclusion.', language: 'te', isStatic: true }
      ]
    },
    {
      title: 'Watch It Again',
      items: [
        { id: 27205, title: 'Inception', thumbnail: blackPoster, description: 'Mind-bending heist.', language: 'en', isStatic: false },
        { id: 19995, title: 'Avatar', thumbnail: blackPoster, description: 'Sci-fi epic.', language: 'en', isStatic: false }
      ]
    }
  ];

  useEffect(() => {
    localStorage.setItem('myList', JSON.stringify(favorites));
  }, [favorites]);

  const toggleFavorite = (item) => {
    setFavorites((prev) => {
      const isFavorited = prev.some((fav) => fav.id === item.id);
      return isFavorited ? prev.filter((fav) => fav.id !== item.id) : [...prev, item];
    });
  };

  const fetchRecommendations = async (movieTitle, isTelugu = false, retries = 2) => {
    if (!movieTitle || recommendations[movieTitle]) return;
    setLoading(true);
    for (let attempt = 0; attempt <= retries; attempt++) {
      try {
        const endpoint = isTelugu ? 'random-telugu' : 'recommend';
        const response = await fetch(`http://localhost:5000/${endpoint}`, {
          method: endpoint === 'recommend' ? 'POST' : 'GET',
          headers: { 'Content-Type': 'application/json' },
          body: endpoint === 'recommend' ? JSON.stringify({ movie_title: movieTitle }) : undefined,
          signal: AbortSignal.timeout(5000) // 5s timeout
        });
        if (!response.ok) {
          throw new Error(`HTTP error: ${response.status}`);
        }
        const data = await response.json();
        if (!Array.isArray(data) || data.length === 0) {
          throw new Error('Empty or invalid response');
        }
        const recs = data.slice(0, 5).map((rec, i) => ({
          id: rec.id || `temp-${i + 100}`,
          title: rec.title || 'Unknown',
          thumbnail: rec.poster || placeholderPoster,
          description: `Language: ${rec.original_language || 'en'}`,
          language: rec.original_language || 'en',
          isStatic: false
        }));
        setRecommendations((prev) => ({ ...prev, [movieTitle]: recs }));
        setError(null);
        return; // Success
      } catch (err) {
        console.error(`Attempt ${attempt + 1} failed for "${movieTitle}":`, err);
        if (attempt === retries) {
          setError(`Failed to load recommendations for ${movieTitle} after ${retries + 1} attempts`);
        }
        await new Promise((resolve) => setTimeout(resolve, 1000)); // Wait 1s before retry
      }
    }
    setLoading(false);
  };

  const fetchPoster = async (movieTitle) => {
    if (staticMoviesWithLocalPosters[movieTitle]) {
      return staticMoviesWithLocalPosters[movieTitle];
    }
    if (posterCache.has(movieTitle)) {
      return posterCache.get(movieTitle);
    }
    try {
      const response = await fetch('http://localhost:5000/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: movieTitle }),
        signal: AbortSignal.timeout(5000)
      });
      if (!response.ok) {
        throw new Error(`HTTP error: ${response.status}`);
      }
      const data = await response.json();
      const poster = Array.isArray(data) && data[0]?.poster ? data[0].poster : placeholderPoster;
      posterCache.set(movieTitle, poster);
      return poster;
    } catch (err) {
      console.error(`Error fetching poster for "${movieTitle}":`, err);
      posterCache.set(movieTitle, placeholderPoster);
      return placeholderPoster;
    }
  };

  useEffect(() => {
    const init = async () => {
      setLoading(true);
      try {
        const updatedRows = JSON.parse(JSON.stringify(initialRowsData));
        for (const row of updatedRows) {
          if (row.title === 'Watch It Again') {
            for (const item of row.items) {
              if (!item.isStatic) {
                item.thumbnail = await fetchPoster(item.title);
              }
            }
          }
        }
        setInitialRows(updatedRows);
        await fetchRecommendations(featuredItem.title, true);
      } catch (err) {
        console.error('Error initializing rows:', err);
        setError('Failed to initialize movie rows');
      } finally {
        setLoading(false);
      }
    };
    init();
  }, []);

  const handleMoreInfo = (item) => {
    const updatedItem = {
      ...item,
      thumbnail: item.isStatic ? staticMoviesWithLocalPosters[item.title] || placeholderPoster : item.thumbnail
    };
    setSelectedItem(updatedItem);
    fetchRecommendations(item.title, item.language === 'te');
  };

  return (
    <div className="home">
      <Header />
      <Hero item={featuredItem} onMoreInfo={handleMoreInfo} favorites={favorites} toggleFavorite={toggleFavorite} />
      {error && <div className="error" style={{ color: 'red', textAlign: 'center', padding: '10px' }}>{error}</div>}
      {initialRows.map((row, index) => (
        <Row
          key={`row-${index}`}
          title={row.title}
          items={row.items}
          onMoreInfo={handleMoreInfo}
          favorites={favorites}
          toggleFavorite={toggleFavorite}
        />
      ))}
      {Object.entries(recommendations).map(([title, recs], index) => (
        <Row
          key={`rec-row-${index}`}
          title={`Because you watched ${title}`}
          items={recs}
          onMoreInfo={handleMoreInfo}
          favorites={favorites}
          toggleFavorite={toggleFavorite}
        />
      ))}
      {loading && <div className="loading" style={{ textAlign: 'center', padding: '20px' }}>Loading...</div>}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      <Footer />
    </div>
  );
}

export default Home;
