import React, { useState, useEffect, useRef } from 'react';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Search.css';

function Search() {
  const [searchTitle, setSearchTitle] = useState('');
  const [results, setResults] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const suggestionCache = useRef(new Map());
  const debounceTimeout = useRef(null);

  const fetchWithTimeout = async (url, options, timeout = 15000) => {
    const controller = new AbortController();
    const id = setTimeout(() => controller.abort(), timeout);
    try {
      const response = await fetch(url, { ...options, signal: controller.signal });
      clearTimeout(id);
      return response;
    } catch (err) {
      clearTimeout(id);
      throw new Error(`Network error: ${err.message}`);
    }
  };

  const fetchSuggestions = async (query) => {
    if (!query.trim()) {
      setSuggestions([]);
      setError(null);
      return;
    }

    if (suggestionCache.current.has(query)) {
      setSuggestions(suggestionCache.current.get(query));
      setError(null);
      return;
    }

    try {
      const response = await fetchWithTimeout(
        'http://localhost:5000/autocomplete',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ query }),
        },
        15000
      );

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || `HTTP error: ${response.status}`);
      }

      const data = await response.json();
      if (data.error) {
        setError(`Autocomplete failed: ${data.error}`);
        setSuggestions([]);
      } else {
        suggestionCache.current.set(query, data);
        setSuggestions(data);
        setError(null);
        data.forEach(s => {
          if (s.poster === 'https://via.placeholder.com/200x300?text=Poster') {
            console.warn(`Placeholder poster for ${s.title} (ID: ${s.id}) in autocomplete`);
          } else if (!s.poster.startsWith('https://image.tmdb.org')) {
            console.warn(`Invalid poster URL for ${s.title} (ID: ${s.id}): ${s.poster}`);
          }
        });
      }
    } catch (err) {
      console.error('Autocomplete error:', err);
      setError(`Failed to load suggestions: ${err.message}`);
      setSuggestions([]);
    }
  };

  const handleInputChange = (e) => {
    const query = e.target.value;
    setSearchTitle(query);
    setResults([]);
    setError(null);

    if (debounceTimeout.current) {
      clearTimeout(debounceTimeout.current);
    }

    debounceTimeout.current = setTimeout(() => {
      fetchSuggestions(query);
    }, 150);
  };

  const handleInputFocus = () => {
    setSuggestions([]);
    setResults([]);
    setError(null);
  };

  const handleSuggestionClick = (suggestion) => {
    setSearchTitle(suggestion.title);
    setSuggestions([]);
    handleSearch(suggestion.title);
  };

  const handleSearch = async (title = searchTitle) => {
    if (!title.trim()) {
      setError('Please enter a movie title');
      setResults([]);
      setSuggestions([]);
      return;
    }

    setLoading(true);
    setError(null);
    setSuggestions([]);

    try {
      // Fetch exact match
      const movieResponse = await fetchWithTimeout(
        'http://localhost:5000/movie',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ title }),
        },
        18000
      );

      let exactMatch = null;
      if (movieResponse.ok) {
        exactMatch = await movieResponse.json();
        if (exactMatch.error) {
          console.warn(`Movie endpoint error: ${exactMatch.error}`);
        } else {
          if (exactMatch.poster === 'https://via.placeholder.com/200x300?text=Poster') {
            console.warn(`Placeholder poster for ${exactMatch.title} (ID: ${exactMatch.id}) in movie endpoint`);
          } else if (!exactMatch.poster.startsWith('https://image.tmdb.org')) {
            console.warn(`Invalid poster URL for ${exactMatch.title} (ID: ${exactMatch.id}): ${exactMatch.poster}`);
          }
        }
      } else {
        const errorData = await movieResponse.json().catch(() => ({}));
        console.warn(`Movie endpoint failed: ${errorData.error || movieResponse.status}`);
      }

      // Fetch recommendations
      const recommendResponse = await fetchWithTimeout(
        'http://localhost:5000/recommend',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ movie_title: title }),
        },
        18000
      );

      let recommendations = [];
      if (recommendResponse.ok) {
        recommendations = await recommendResponse.json();
        if (!Array.isArray(recommendations)) {
          console.warn(`Recommend endpoint error: ${recommendations.error || 'Invalid response'}`);
        } else {
          recommendations.forEach(rec => {
            if (rec.poster === 'https://via.placeholder.com/200x300?text=Poster') {
              console.warn(`Placeholder poster for ${rec.title} (ID: ${rec.id}) in recommend endpoint`);
            } else if (!rec.poster.startsWith('https://image.tmdb.org')) {
              console.warn(`Invalid poster URL for ${rec.title} (ID: ${rec.id}): ${rec.poster}`);
            }
          });
        }
      } else {
        const errorData = await recommendResponse.json().catch(() => ({}));
        console.warn(`Recommend endpoint failed: ${errorData.error || recommendResponse.status}`);
      }

      if (exactMatch && !exactMatch.error) {
        const mappedResults = [
          {
            id: exactMatch.id,
            title: exactMatch.title,
            thumbnail: exactMatch.poster || 'https://via.placeholder.com/200x300?text=Poster',
            description: `Language: ${exactMatch.original_language || 'N/A'}, Genres: ${exactMatch.genres || 'N/A'}`
          },
          ...recommendations.map((rec, i) => ({
            id: rec.id || i + 100,
            title: rec.title || 'Unknown Title',
            thumbnail: rec.poster || 'https://via.placeholder.com/200x300?text=Poster',
            description: `Language: ${rec.original_language || 'N/A'}, Genres: ${rec.genres || 'N/A'}`
          }))
        ];
        setResults(mappedResults.slice(0, 6));
      } else if (recommendations.length > 0) {
        const mappedResults = recommendations.map((rec, i) => ({
          id: rec.id || i + 100,
          title: rec.title || 'Unknown Title',
          thumbnail: rec.poster || 'https://via.placeholder.com/200x300?text=Poster',
          description: `Language: ${rec.original_language || 'N/A'}, Genres: ${rec.genres || 'N/A'}`
        }));
        setResults(mappedResults.slice(0, 5));
        setError('Exact movie not found, showing related movies');
      } else {
        setError('No results found');
        setResults([]);
      }
    } catch (err) {
      console.error('Search error:', err);
      setError(`Search failed: ${err.message}`);
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  const handleMoreInfo = (item) => {
    setSelectedItem(item);
  };

  const handleClickOutside = (e) => {
    if (!e.target.closest('.search-bar') && !e.target.closest('.suggestions')) {
      setSuggestions([]);
    }
  };

  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, []);

  return (
    <div className="search">
      <div className="search-container">
        <div className="search-bar">
          <input
            type="text"
            value={searchTitle}
            onChange={handleInputChange}
            onFocus={handleInputFocus}
            placeholder="Search for a movie"
          />
          <button onClick={() => handleSearch()}>Search</button>
          {suggestions.length > 0 && (
            <ul className="suggestions">
              {suggestions.map((suggestion) => (
                <li
                  key={suggestion.id}
                  className="suggestion-item"
                  onClick={() => handleSuggestionClick(suggestion)}
                >
                  <img
                    src={suggestion.poster}
                    alt={suggestion.title}
                    className="suggestion-poster"
                  />
                  <span>{suggestion.title}</span>
                </li>
              ))}
            </ul>
          )}
        </div>
        {loading && <div className="loading">Loading...</div>}
        {error && <div className="error">{error}</div>}
        {results.length > 0 && (
          <Row
            title={`Search Results for "${searchTitle}"`}
            items={results}
            onMoreInfo={handleMoreInfo}
          />
        )}
        {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      </div>
      <Footer />
    </div>
  );
}

export default Search;
