import React, { useState, useEffect, useRef } from 'react';
import './Hero.css';

function Hero({ item, onMoreInfo }) {
  const [showVideo, setShowVideo] = useState(false);
  const videoRef = useRef(null);

  useEffect(() => {
    console.log('Hero item:', item);
    if (!item || !item.video) {
      console.error('No video provided:', item);
      return;
    }

    console.log('Video URL:', item.video);
    const timer = setTimeout(() => {
      console.log(`Showing video: ${item.title}`);
      setShowVideo(true);
    }, 3000);

    return () => clearTimeout(timer);
  }, [item]);

  useEffect(() => {
    if (showVideo && videoRef.current) {
      console.log('Attempting to play video');
      videoRef.current.volume = 1.0;
      videoRef.current.play()
        .then(() => console.log('Video playing'))
        .catch(err => {
          console.error('Play error:', err.name, err.message);
          if (err.name === 'NotAllowedError') {
            console.warn('Autoplay blocked');
          }
        });
    }
  }, [showVideo]);

  if (!item) return null;

  return (
    <div className="hero">
      {showVideo && item.video ? (
        <video
          ref={videoRef}
          src={item.video}
          autoPlay
          loop
          playsInline
          className="hero-video"
          onError={e => console.error('Video error:', e.target.error?.code, e.target.error?.message)}
          onCanPlay={() => console.log('Video ready')}
          onPlay={() => console.log('Video started')}
        >
          <source src={item.video} type="video/mp4" />
          Your browser does not support video.
        </video>
      ) : showVideo ? (
        <div className="video-fallback">
          <img src={item.thumbnail} alt="Video failed" />
          <p>Video failed. Check console.</p>
        </div>
      ) : (
        <div
          className="hero-background"
          style={{ backgroundImage: item.thumbnail ? `url(${item.thumbnail})` : 'none' }}
        />
      )}
      <div className="hero-content">
        <h1 className="hero-title">{item.title}</h1>
        <p className="hero-description">{item.description}</p>
        <div className="hero-buttons">
          <button className="btn info-btn" onClick={() => onMoreInfo(item)}>
            <i className="fas fa-info-circle" /> More Info
          </button>
        </div>
      </div>
    </div>
  );
}

export default Hero;
