import React, { useState, useEffect } from 'react';
import './Terms.css';

const defaultTerms = [
  { section: '1. Acceptance', content: 'By using StreamFlix, you agree to these terms.' },
  { section: '2. Subscription', content: 'Subscriptions are monthly. Cancel anytime.' },
  { section: '3. Privacy', content: 'Your privacy is our priority. We do not sell your data.' },
  { section: '4. Content Use', content: 'Only personal use is allowed.' },
  { section: '5. Termination', content: 'Your account may be terminated upon violations.' },
  { section: '6. User Conduct', content: 'Do not misuse the platform.' },
  { section: '7. Payments', content: 'All payments are secure and handled through trusted providers.' },
  { section: '8. Cancellation', content: 'You can cancel your subscription anytime.' },
  { section: '9. Refunds', content: 'Refunds are processed as per the policy.' },
  { section: '10. Changes', content: 'We may update these terms from time to time.' },
  { section: '11. Governing Law', content: 'These terms are governed by applicable law.' },
  { section: '12. Contact', content: 'Reach us at support@streamflix.com.' }
];

function Terms() {
  const [terms, setTerms] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(0);

  useEffect(() => {
    // Simulate API load
    setTimeout(() => {
      setTerms(defaultTerms);
    }, 100);
  }, []);

  return (
    <div className="terms-page">
      <h1>Terms of Use</h1>
      <div className="terms-container">
        <div className="terms-sidebar">
          {terms.map((term, idx) => (
            <div
              key={idx}
              className={`sidebar-item ${idx === selectedIndex ? 'active' : ''}`}
              onClick={() => setSelectedIndex(idx)}
            >
              {term.section}
            </div>
          ))}
        </div>
        <div className="terms-content">
          {terms[selectedIndex] && (
            <>
              <h2>{terms[selectedIndex].section}</h2>
              <p>{terms[selectedIndex].content}</p>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

export default Terms;
