import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Hero from '../components/Hero';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Home.css';

function Home() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [recommendations, setRecommendations] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const featuredItem = {
    id: 1,
    title: "Gandhi",
    description: "A biographical film about Mahatma Gandhi.",
    thumbnail: "https://via.placeholder.com/1280x720?text=Gandhi",
  };

  const initialRows = [
    {
      title: "Trending Now",
      items: [
        { id: 2, title: "Gandhi, My Father", thumbnail: "https://via.placeholder.com/300x450?text=Gandhi,+My+Father", description: "A story of Gandhi's relationship with his son." },
        { id: 3, title: "Avatar", thumbnail: "https://via.placeholder.com/300x450?text=Avatar", description: "A 3D epic set on Pandora." },
        { id: 4, title: "The Dark Knight", thumbnail: "https://via.placeholder.com/300x450?text=The+Dark+Knight", description: "A superhero thriller." },
      ],
    },
    {
      title: "Watch It Again",
      items: [
        { id: 5, title: "Inception", thumbnail: "https://via.placeholder.com/300x450?text=Inception", description: "A mind-bending heist movie." },
        { id: 6, title: "Titanic", thumbnail: "https://via.placeholder.com/300x450?text=Titanic", description: "A romantic disaster film." },
      ],
    },
  ];

  const fetchRecommendations = async (movieTitle) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:5001/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: movieTitle }),
      });
      const data = await response.json();
      if (response.ok) {
        const mappedRecommendations = data.map((rec, i) => ({
          id: i + 100,
          title: rec.title,
          thumbnail: rec.poster || `https://via.placeholder.com/200x450?text=${rec.title.replace(' ', '+')}`, // Fallback placeholder
          description: `Language: ${rec.original_language}, Genres: ${rec.genres || 'N/A'}`,
        }));
        setRecommendations((prev) => ({ ...prev, [movieTitle]: mappedRecommendations }));
      } else {
        setError(data.error || 'Failed to fetch recommendations');
      }
    } catch (err) {
      setError('An error occurred while fetching recommendations');
    } finally {
      setLoading(false);
    }
  };

  const handleMoreInfo = (item) => {
    setSelectedItem(item);
    if (!recommendations[item.title]) {
      fetchRecommendations(item.title);
    }
  };

  return (
    <div className="home">
      <Header />
      <Hero item={featuredItem} onMoreInfo={handleMoreInfo} />
      {initialRows.map((row, index) => (
        <Row key={index} title={row.title} items={row.items} onMoreInfo={handleMoreInfo} />
      ))}
      {Object.entries(recommendations).map(([title, recs]) =>
        recs.length > 0 && (
          <Row
            key={`rec-${title}`}
            title={`Recommendations for ${title}`}
            items={recs}
            onMoreInfo={handleMoreInfo}
          />
        )
      )}
      {loading && <div className="loading">Loading recommendations...</div>}
      {error && <div className="error">{error}</div>}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      <Footer />
    </div>
  );
}

export default Home;
