import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Hero from '../components/Hero';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Home.css';

function Home() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [recommendations, setRecommendations] = useState(() => {
    const saved = sessionStorage.getItem('recommendations');
    return saved ? JSON.parse(saved) : {};
  });
  const [initialRows, setInitialRows] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [favorites, setFavorites] = useState(() =>
    JSON.parse(localStorage.getItem('myList') || '[]')
  );
  const [notification, setNotification] = useState('');

  // Featured item for Baahubali: The Beginning
  const featuredItem = {
    id: 301344,
    title: 'Baahubali: The Beginning',
    description: 'A Telugu epic about valor and sacrifice.',
    thumbnail: null, // Will be fetched dynamically
    video: '/videos/bb2.mp4',
    language: 'te',
    genres: ['Action', 'Period', 'Epic'],
  };

  // Trending Telugu movies aligned with backend
  const initialRowsData = [
    {
      title: 'Trending Movies',
      items: [
        { id: 900352, title: 'Kalki 2898 AD', thumbnail: null, description: 'A futuristic film inspired by Hindu mythology.', language: 'te', genres: ['Sci-Fi', 'Mythology', 'Action'] },
        { id: 934632, title: 'Salaar: Part 1 – Ceasefire', thumbnail: null, description: 'An action-packed saga of power and betrayal.', language: 'te', genres: ['Action', 'Drama', 'Epic'] },
        { id: 614933, title: 'RRR', thumbnail: null, description: 'A revolutionary tale of friendship and rebellion.', language: 'te', genres: ['Action', 'Historical', 'Drama', 'Epic'] },
        { id: 614934, title: 'Pushpa 2: The Rule', thumbnail: null, description: 'A gripping sequel to the smuggling saga.', language: 'te', genres: ['Action', 'Drama'] },
        { id: 1121402, title: 'Hi Nanna', thumbnail: null, description: 'A heartwarming family drama.', language: 'te', genres: ['Romance', 'Drama'] },
        { id: 1080802, title: 'Saripodhaa Sanivaaram', thumbnail: null, description: 'An intense vigilante story.', language: 'te', genres: ['Action', 'Thriller', 'Vigilante'] },
        { id: 614932, title: 'Saaho', thumbnail: null, description: 'A high-octane action thriller.', language: 'te', genres: ['Action', 'Thriller'] },
      ],
    },
    {
      title: 'Watch It Again',
      items: [
        { id: 5, title: 'Inception', thumbnail: null, description: 'A mind-bending heist movie.', language: 'en', genres: ['Sci-Fi', 'Thriller'] },
        { id: 6, title: 'Titanic', thumbnail: null, description: 'A romantic disaster film.', language: 'en', genres: ['Romance', 'Drama'] },
      ],
    },
  ];

  // Clear recommendations from sessionStorage on page unload
  useEffect(() => {
    const handleBeforeUnload = () => {
      sessionStorage.removeItem('recommendations');
    };
    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => window.removeEventListener('beforeunload', handleBeforeUnload);
  }, []);

  // Save favorites to localStorage
  useEffect(() => {
    window.scrollTo(0, 0);
    localStorage.setItem('myList', JSON.stringify(favorites));
  }, [favorites]);

  // Save recommendations to sessionStorage
  useEffect(() => {
    sessionStorage.setItem('recommendations', JSON.stringify(recommendations));
  }, [recommendations]);

  const toggleFavorite = (item) => {
    if (!item.id || !item.title) {
      setError('Invalid movie item');
      return;
    }
    setFavorites((prevFavorites) => {
      const isFavorited = prevFavorites.some((fav) => fav.id === item.id);
      if (isFavorited) {
        const updatedFavorites = prevFavorites.filter((fav) => fav.id !== item.id);
        setNotification(`"${item.title}" removed from favorites`);
        setTimeout(() => setNotification(''), 3000);
        return updatedFavorites;
      } else {
        const newFavorite = {
          id: item.id,
          title: item.title,
          thumbnail: item.thumbnail,
          description: item.description,
          language: item.language,
          genres: item.genres || [],
        };
        setNotification(`"${item.title}" added to favorites`);
        setTimeout(() => setNotification(''), 3000);
        return [...prevFavorites, newFavorite];
      }
    });
  };

  const fetchPosterForMovie = async (movieTitle, movieId, year = null) => {
    try {
      const response = await fetch('http://localhost:5000/movie', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ title: movieTitle }),
      });
      if (!response.ok) {
        throw new Error(`HTTP error: ${response.status}`);
      }
      const data = await response.json();
      if (data.poster && data.poster !== 'https://via.placeholder.com/200x300') {
        return data.poster;
      }
      return 'https://via.placeholder.com/200x300';
    } catch (err) {
      console.error(`Error fetching poster for "${movieTitle}":`, err);
      return 'https://via.placeholder.com/200x300';
    }
  };

  const fetchRecommendations = async (movieTitle, language, genres = []) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:5000/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: movieTitle }),
      });
      const data = await response.json();
      if (response.ok && Array.isArray(data) && data.length > 0) {
        const mappedRecommendations = data.slice(0, 5).map((rec, i) => ({
          id: rec.id || i + 100,
          title: rec.title,
          thumbnail: rec.poster || 'https://via.placeholder.com/200x300',
          description: `Language: ${rec.original_language || language || 'en'}, Genres: ${rec.genres || 'N/A'}`,
          language: rec.original_language || language || 'en',
          genres: rec.genres ? rec.genres.split(' ') : genres,
        }));
        setRecommendations((prev) => ({ ...prev, [movieTitle]: mappedRecommendations }));
      } else {
        setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
      }
    } catch (err) {
      setError(`Failed to fetch recommendations for "${movieTitle}": ${err.message}`);
      setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const fetchInitialData = async () => {
      setLoading(true);
      try {
        const updatedRows = JSON.parse(JSON.stringify(initialRowsData));
        for (const row of updatedRows) {
          for (const item of row.items) {
            if (!item.thumbnail) {
              item.thumbnail = await fetchPosterForMovie(item.title, item.id);
            }
          }
        }
        setInitialRows(updatedRows);

        if (featuredItem.language === 'te' && !recommendations[featuredItem.title]) {
          await fetchRecommendations(featuredItem.title, featuredItem.language, featuredItem.genres);
        }
      } catch (err) {
        setError('Failed to fetch initial data');
      } finally {
        setLoading(false);
      }
    };

    fetchInitialData();
  }, [recommendations]);

  const handleMoreInfo = (item) => {
    const updatedItem = { ...item };
    setSelectedItem(updatedItem);
    fetchRecommendations(item.title, item.language, item.genres);
  };

  return (
    <div className="home">
      <Header />
      <Hero
        item={{ ...featuredItem, thumbnail: featuredItem.thumbnail || 'https://via.placeholder.com/200x300' }}
        onMoreInfo={handleMoreInfo}
        favorites={favorites}
        toggleFavorite={toggleFavorite}
      />
      {initialRows.map((row, index) => (
        <Row
          key={index}
          title={row.title}
          items={row.items}
          onMoreInfo={handleMoreInfo}
          favorites={favorites}
          toggleFavorite={toggleFavorite}
        />
      ))}
      {Object.entries(recommendations).map(([title, recs]) => (
        recs.length > 0 && (
          <Row
            key={`rec-${title}`}
            title={`Recommendations for ${title}`}
            items={recs}
            onMoreInfo={handleMoreInfo}
            favorites={favorites}
            toggleFavorite={toggleFavorite}
          />
        )
      ))}
      {loading && <div className="loading">Loading...</div>}
      {error && (
        <div className="error">
          {error}
          <button onClick={() => setError(null)}>Dismiss</button>
        </div>
      )}
      {notification && <div className="notification">{notification}</div>}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      <Footer />
    </div>
  );
}

export default Home;
