import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Footer from '../components/Footer';
import './Terms.css';

function Terms() {
  const [terms, setTerms] = useState([]);
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [error, setError] = useState('');

  useEffect(() => {
    window.scrollTo(0, 0);
    const fetchTerms = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/terms');
        setTerms(response.data);
      } catch (err) {
        setError('Failed to load terms. Showing default content.');
        setTerms([
          { section: '1. Acceptance', content: 'By using StreamFlix, you agree to these terms.' },
          { section: '2. Subscription', content: 'Subscriptions are monthly. Cancel anytime from your profile.' },
          { section: '3. Privacy', content: 'Your privacy is important. We do not sell your data.' },
          { section: '4. Content Use', content: 'Content is for personal, non-commercial use only.' },
          { section: '5. Termination', content: 'Accounts may be terminated on policy violations.' },
          { section: '6. Support', content: 'Contact support via the help center for any queries.' },
        ]);
      }
    };
    fetchTerms();
  }, []);

  const handleSelect = (index) => {
    setSelectedIndex(index);
  };

  return (
    <div className="terms-container">
      <h1>Terms of Use</h1>
      {error && <p className="error">{error}</p>}
      <div className="terms-layout">
        <aside className="terms-sidebar">
          {terms.map((term, index) => (
            <button
              key={index}
              className={`term-title ${selectedIndex === index ? 'active' : ''}`}
              onClick={() => handleSelect(index)}
            >
              {term.section}
            </button>
          ))}
        </aside>

        <section className="terms-content">
          {terms[selectedIndex] && (
            <>
              <h2>{terms[selectedIndex].section}</h2>
              <p>{terms[selectedIndex].content}</p>
            </>
          )}
        </section>
      </div>
      <Footer />
    </div>
  );
}

export default Terms;
