import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Hero from '../components/Hero';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Home.css';

// Import posters
import kalkiPoster from '../data/Kalki.jpg';
import salaarPoster from '../data/salaar.jpg';
import chaavaPoster from '../data/chaava.jpg';
import rrrPoster from '../data/rrr.jpg';
import pushpa2Poster from '../data/pushpa2.jpg';
import hiNannaPoster from '../data/hinanna.jpg';
import mad2Poster from '../data/mad2.jpg';
import courtPoster from '../data/court.jpg';
import dragonPoster from '../data/rod.jpg';
import saripodaPoster from '../data/ss.jpg';
import saahoPoster from '../data/saaho.jpg';
import baahubaliPoster from '../data/bb2.jpg';
import blackPoster from '../data/black.jpg';

function Home() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [recommendations, setRecommendations] = useState({});
  const [initialRows, setInitialRows] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [favorites, setFavorites] = useState(() => JSON.parse(localStorage.getItem('myList') || '[]'));
  const [notification, setNotification] = useState('');

  const staticMoviesWithLocalPosters = {
    "Chaava": chaavaPoster,
    "Mad2": mad2Poster,
    "Saripoda Sanivaaram": saripodaPoster,
  };

  const featuredItem = {
    id: 271726,
    title: "Baahubali: The Beginning",
    description: "A Telugu epic about valor and sacrifice.",
    thumbnail: baahubaliPoster,
    video: "/videos/bb2.mp4",
    language: "te",
    genre: "Action"
  };

  const initialRowsData = [
    {
      title: "Trending Movies",
      items: [
        { id: 900352, title: "Kalki 2898 AD", thumbnail: kalkiPoster, description: "A sci-fi epic set in a dystopian future.", language: "te", genre: "Sci-Fi" },
        { id: 934632, title: "Salaar", thumbnail: salaarPoster, description: "An action-packed saga of power and betrayal.", language: "te", genre: "Action" },
        { id: 0, title: "Chaava", thumbnail: chaavaPoster, description: "A historical drama about bravery.", isStatic: true, language: "te", genre: "Drama" },
        { id: 614933, title: "RRR", thumbnail: rrrPoster, description: "A revolutionary tale of friendship and rebellion.", language: "te", genre: "Action" },
        { id: 614934, title: "Pushpa 2: The Rule", thumbnail: pushpa2Poster, description: "A gripping sequel to the smuggling saga.", language: "te", genre: "Action" },
        { id: 1121402, title: "Hi Nanna", thumbnail: hiNannaPoster, description: "A heartwarming family drama.", language: "te", genre: "Drama" },
        { id: 1, title: "Mad2", thumbnail: mad2Poster, description: "A thrilling continuation of mystery.", isStatic: true, language: "te", genre: "Thriller" },
        { id: 297282, title: "Court", thumbnail: courtPoster, description: "A thought-provoking legal drama.", language: "te", genre: "Drama" },
        { id: 1560, title: "Return of the Dragon", thumbnail: dragonPoster, description: "A classic martial arts adventure.", language: "te", genre: "Action" },
        { id: 614932, title: "Saaho", thumbnail: saahoPoster, description: "A high-octane action thriller.", language: "te", genre: "Action" },
        { id: 2, title: "Saripoda Sanivaaram", thumbnail: saripodaPoster, description: "An intense vigilante story.", isStatic: true, language: "te", genre: "Action" },
      ],
    },
    {
      title: "Watch It Again",
      items: [
        { id: 5, title: "Inception", thumbnail: blackPoster, description: "A mind-bending heist movie.", language: "en", genre: "Sci-Fi" },
        { id: 6, title: "Titanic", thumbnail: blackPoster, description: "A romantic disaster film.", language: "en", genre: "Romance" },
      ],
    },
  ];

  // Clear sessionStorage on page load (refresh)
  useEffect(() => {
    console.log('Creating new session: Clearing sessionStorage on page load (refresh)');
    sessionStorage.removeItem('recommendations');
    setRecommendations({});
  }, []);

  // Clear sessionStorage on tab close
  useEffect(() => {
    const handleBeforeUnload = () => {
      console.log('Ending session: Clearing sessionStorage on tab close');
      sessionStorage.removeItem('recommendations');
    };
    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, []);

  // Load recommendations from sessionStorage on mount
  useEffect(() => {
    const savedRecommendations = sessionStorage.getItem('recommendations');
    if (savedRecommendations) {
      console.log('Loading recommendations from sessionStorage:', JSON.parse(savedRecommendations));
      setRecommendations(JSON.parse(savedRecommendations));
    }
  }, []);

  // Save recommendations to sessionStorage when they change
  useEffect(() => {
    if (Object.keys(recommendations).length > 0) {
      console.log('Saving recommendations to sessionStorage:', recommendations);
      sessionStorage.setItem('recommendations', JSON.stringify(recommendations));
    }
  }, [recommendations]);

  useEffect(() => {
    window.scrollTo(0, 0);
    localStorage.setItem('myList', JSON.stringify(favorites));
    console.log('Favorites saved to localStorage:', favorites);
  }, [favorites]);

  const toggleFavorite = (item) => {
    setFavorites((prevFavorites) => {
      const isFavorited = prevFavorites.some((fav) => fav.id === item.id);
      if (isFavorited) {
        console.log('Removing from favorites:', item.title);
        const updatedFavorites = prevFavorites.filter((fav) => fav.id !== item.id);
        setNotification(`"${item.title}" removed from favorites`);
        setTimeout(() => setNotification(''), 3000);
        return updatedFavorites;
      } else {
        setNotification(`"${item.title}" added to favorites`);
        setTimeout(() => setNotification(''), 3000);
        console.log('Adding to favorites:', item.title);
        return [...prevFavorites, item];
      }
    });
  };

  const fetchPosterForMovie = async (movieTitle) => {
    try {
      console.log(`Fetching poster for "${movieTitle}" via /recommend endpoint...`);
      const response = await fetch('http://localhost:5000/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: movieTitle }),
      });
      if (!response.ok) {
        console.warn(`Failed to fetch poster for "${movieTitle}": HTTP status ${response.status}, ${response.statusText}`);
        return blackPoster;
      }
      const data = await response.json();
      console.log(`Poster fetch response for "${movieTitle}":`, data);
      if (Array.isArray(data) && data.length > 0) {
        const firstMovie = data[0];
        if (firstMovie.poster && typeof firstMovie.poster === 'string' && firstMovie.poster.trim() !== '') {
          console.log(`Valid poster found for "${movieTitle}":`, firstMovie.poster);
          return firstMovie.poster;
        } else {
          console.warn(`No valid poster field in response for "${movieTitle}":`, firstMovie);
          return blackPoster;
        }
      } else {
        console.warn(`Empty or invalid response for "${movieTitle}" when fetching poster`);
        return blackPoster;
      }
    } catch (err) {
      console.error(`Error fetching poster for "${movieTitle}":`, err);
      return blackPoster;
    }
  };

  const fetchTeluguRecommendations = async (movieTitle, genre, numMovies) => {
    if (recommendations[movieTitle]) {
      console.log(`Recommendations for "${movieTitle}" already exist in session, skipping fetch.`);
      return;
    }
    setLoading(true);
    setError(null);
    let recommendationsData = [];

    try {
      console.log(`Fetching ${numMovies} Telugu recommendations for "${movieTitle}" (Genre: ${genre})...`);
      const requestBody = { movie_title: movieTitle, genre, language: 'te' };
      console.log(`Request body:`, requestBody);
      const response = await fetch('http://localhost:5000/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestBody),
      });
      console.log(`Fetch response for "${movieTitle}": Status ${response.status}`);
      if (!response.ok) {
        console.warn(`Fetch failed for "${movieTitle}": Status ${response.status}, ${response.statusText}`);
      } else {
        const data = await response.json();
        console.log(`Fetch data for "${movieTitle}":`, data);
        if (Array.isArray(data) && data.length > 0) {
          recommendationsData = data;
        }
      }

      // Process recommendations
      if (recommendationsData.length > 0) {
        const uniqueData = Array.from(new Map(recommendationsData.map(item => [item.title.toLowerCase(), item])).values());
        console.log(`After removing duplicates, ${uniqueData.length} unique Telugu recommendations for "${movieTitle}":`, uniqueData);
        const shuffled = uniqueData.sort(() => 0.5 - Math.random());
        recommendationsData = shuffled.slice(0, Math.min(numMovies, uniqueData.length)).map((rec, i) => ({
          id: i + 200,
          title: rec.title,
          thumbnail: rec.poster || blackPoster,
          description: `Language: te, Genres: ${rec.genres || genre || 'N/A'}`,
          language: 'te',
          genre: rec.genres || genre
        }));
        console.log(`Processed recommendations for "${movieTitle}":`, recommendationsData);
      } else {
        console.warn(`No recommendations received for "${movieTitle}"`);
      }

      setRecommendations((prev) => ({ ...prev, [movieTitle]: recommendationsData }));
    } catch (err) {
      console.error(`Network error fetching recommendations for "${movieTitle}":`, err.message);
      setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
    } finally {
      setLoading(false);
    }
  };

  const fetchRecommendations = async (movieTitle) => {
    if (recommendations[movieTitle]) {
      console.log(`Recommendations for "${movieTitle}" already exist in session, skipping fetch.`);
      return;
    }
    setLoading(true);
    setError(null);
    try {
      console.log(`Fetching recommendations for "${movieTitle}"...`);
      const response = await fetch('http://localhost:5000/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: movieTitle }),
      });
      console.log(`Response for /recommend: status ${response.status}, data:`, data);
      if (!response.ok) {
        console.warn(`Fetch failed for "${movieTitle}": Status ${response.status}, ${response.statusText}`);
        setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
        return;
      }
      const data = await response.json();
      console.log(`Fetch data for "${movieTitle}":`, data);
      if (Array.isArray(data) && data.length > 0) {
        const uniqueData = Array.from(new Map(data.map(item => [item.title.toLowerCase(), item])).values());
        console.log(`After removing duplicates, ${uniqueData.length} unique recommendations for "${movieTitle}":`, uniqueData);
        const numRecs = Math.floor(Math.random() * (10 - 5 + 1)) + 5;
        console.log(`Selecting ${numRecs} recommendations for "${movieTitle}"...`);
        const shuffled = uniqueData.sort(() => 0.5 - Math.random());
        const mappedRecommendations = shuffled.slice(0, Math.min(numRecs, uniqueData.length)).map((rec, i) => ({
          id: i + 100,
          title: rec.title,
          thumbnail: rec.poster || blackPoster,
          description: `Language: ${rec.original_language || 'en'}, Genres: ${rec.genres || 'N/A'}`,
          language: rec.original_language || 'en',
          genre: rec.genres || 'N/A'
        }));
        setRecommendations((prev) => ({ ...prev, [movieTitle]: mappedRecommendations }));
      } else {
        console.warn(`No recommendations received for "${movieTitle}"`);
        setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
      }
    } catch (err) {
      console.error(`Network error fetching recommendations for "${movieTitle}":`, err.message);
      setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const fetchInitialData = async () => {
      setLoading(true);
      try {
        const updatedRows = JSON.parse(JSON.stringify(initialRowsData));
        for (const row of updatedRows) {
          if (row.title === "Watch It Again") {
            for (const item of row.items) {
              if (item.language === "en") {
                item.thumbnail = await fetchPosterForMovie(item.title);
              }
            }
          }
        }
        setInitialRows(updatedRows);
        if (featuredItem.language === "te") {
          const numRecs = Math.floor(Math.random() * (10 - 5 + 1)) + 5;
          await fetchTeluguRecommendations(featuredItem.title, featuredItem.genre, numRecs);
        }
      } catch (err) {
        console.error('Initial fetch error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchInitialData();
  }, []);

  const handleMoreInfo = (item) => {
    const updatedItem = {
      ...item,
      thumbnail: item.isStatic && staticMoviesWithLocalPosters[item.title] ? staticMoviesWithLocalPosters[item.title] : item.thumbnail
    };
    setSelectedItem(updatedItem);
    if (!recommendations[item.title]) {
      if (item.language === "te") {
        const numRecs = Math.floor(Math.random() * (10 - 5 + 1)) + 5;
        fetchTeluguRecommendations(item.title, item.genre || 'Action', numRecs);
      } else {
        fetchRecommendations(item.title);
      }
    }
  };

  return (
    <div className="home">
      <Header />
      <Hero
        item={featuredItem}
        onMoreInfo={handleMoreInfo}
        favorites={favorites || []}
        toggleFavorite={toggleFavorite}
      />
      {(initialRows.length > 0 ? initialRows : initialRowsData).map((row, index) => (
        <Row
          key={index}
          title={row.title}
          items={row.items}
          onMoreInfo={handleMoreInfo}
          favorites={favorites || []}
          toggleFavorite={toggleFavorite}
        />
      ))}
      {Object.entries(recommendations).map(([title, recs]) => (
        <Row
          key={`rec-${title}`}
          title={`Recommendations for ${title}`}
          items={recs}
          onMoreInfo={handleMoreInfo}
          favorites={favorites || []}
          toggleFavorite={toggleFavorite}
        />
      ))}
      {!Object.keys(recommendations).length && !loading && (
        <div className="no-recommendations">No recommendations loaded yet. Click "More Info" to fetch recommendations.</div>
      )}
      {loading && <div className="loading">Loading recommendations...</div>}
      {error && <div className="error">{error}</div>}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      {notification && <div className="notification">{notification}</div>}
      <Footer />
    </div>
  );
}

export default Home;
