import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import Header from '../components/Header';
import Footer from '../components/Footer';
import './Terms.css';

function Terms() {
  const [terms, setTerms] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    window.scrollTo(0, 0);
    const fetchTerms = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/terms');
        setTerms(response.data);
      } catch (err) {
        setError('Failed to load terms. Showing defaults.');
        setTerms([
          { section: 'Acceptance', content: 'By using StreamFlix, you agree to these terms.' },
          { section: 'Subscription', content: 'You may subscribe monthly or yearly. Cancel anytime through your account settings.' },
          { section: 'Content Usage', content: 'StreamFlix content is for personal use only. Redistribution is prohibited.' },
          { section: 'Account Security', content: 'You are responsible for maintaining the confidentiality of your account.' },
          { section: 'Changes', content: 'We may update these terms from time to time. Continued use constitutes acceptance.' },
          { section: 'Termination', content: 'Violation of terms may result in termination of access without prior notice.' },
          { section: 'Support', content: 'For help, contact our support team via email or the support center in your account.' },
        ]);
      }
    };
    fetchTerms();
  }, []);

  return (
    <div className="terms">
      {/* <Header /> */}
      <main>
        <h1>Terms of Use</h1>
        {error && <p className="error">{error}</p>}
        <div className="content-scroll">
          {terms.map((term, index) => (
            <div className="term-block" key={index}>
              <h3>{term.section}</h3>
              <p>{term.content}</p>
            </div>
          ))}
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default Terms;
