import React, { useState } from 'react';
import Header from '../components/Header';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Movies.css';

function Movies() {
  const [searchTitle, setSearchTitle] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleSearch = async () => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:5001/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: searchTitle }),
      });
      const data = await response.json();
      if (response.ok && Array.isArray(data)) {
        const mappedRecommendations = data.map((rec, i) => ({
          id: i + 100,
          title: rec.title,
          thumbnail: rec.poster || 'https://via.placeholder.com/200x300',
          description: `Language: ${rec.original_language || 'N/A'}, Genres: ${rec.genres || 'N/A'}`,
        }));
        setRecommendations(mappedRecommendations);
      } else {
        setError('No recommendations found');
        setRecommendations([]);
      }
    } catch (err) {
      setError('Network error while fetching recommendations');
      console.error('Search error:', err);
      setRecommendations([]);
    } finally {
      setLoading(false);
    }
  };

  const handleMoreInfo = (item) => {
    setSelectedItem(item);
  };

  return (
    <div className="movies">
      {/* <Header /> */}
      <div className="search-bar">
        <input
          type="text"
          value={searchTitle}
          onChange={(e) => setSearchTitle(e.target.value)}
          placeholder="Search for a movie"
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error">{error}</div>}
      {recommendations.length > 0 && (
        <Row
          title={`Recommendations for "${searchTitle}"`}
          items={recommendations}
          onMoreInfo={handleMoreInfo}
        />
      )}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      <Footer />
    </div>
  );
}

export default Movies;
