import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Hero from '../components/Hero';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Home.css';

function Home() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [recommendations, setRecommendations] = useState({});
  const [initialRows, setInitialRows] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const featuredItem = {
    id: 301344, // TMDB ID for Baahubali: The Beginning
    title: "Baahubali: The Beginning",
    description: "A Telugu epic about valor and sacrifice.",
    thumbnail: null, // Will be populated by TMDB poster from backend
  };

  const initialRowsData = [
    {
      title: "Trending Telugu Movies",
      items: [], // Will be populated with top trending Telugu movies
    },
    {
      title: "Trending Now",
      items: [
        { id: 3, title: "Avatar", thumbnail: null, description: "A 3D epic set on Pandora." },
        { id: 4, title: "The Dark Knight", thumbnail: null, description: "A superhero thriller." },
      ],
    },
    {
      title: "Watch It Again",
      items: [
        { id: 5, title: "Inception", thumbnail: null, description: "A mind-bending heist movie." },
        { id: 6, title: "Titanic", thumbnail: null, description: "A romantic disaster film." },
      ],
    },
  ];

  // Fetch posters for initial items on component mount
  useEffect(() => {
    const fetchInitialPosters = async () => {
      setLoading(true);
      try {
        // Fetch trending Telugu movies
        const trendingResponse = await fetch('http://localhost:5001/trending-telugu', {
          method: 'GET',
          headers: { 'Content-Type': 'application/json' },
        });
        const trendingData = await trendingResponse.json();
        if (trendingResponse.ok && trendingData.length > 0) {
          const trendingItems = trendingData.map((movie, i) => ({
            id: movie.id || i + 1000, // Use movie ID or generate unique ID
            title: movie.title,
            thumbnail: movie.poster || null,
            description: `Genres: ${movie.genres || 'N/A'}`,
          }));
          initialRowsData[0].items = trendingItems; // Update Trending Telugu Movies row
        } else {
          console.warn("Failed to fetch trending Telugu movies:", trendingData.error || "No data");
          initialRowsData[0].items = []; // Fallback to empty if no data
        }

        // Fetch posters for other initial items
        const updatedRows = await Promise.all(
          initialRowsData.map(async (row) => {
            const updatedItems = await Promise.all(
              row.items.map(async (item) => {
                const response = await fetch('http://localhost:5001/recommend', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify({ movie_title: item.title }),
                });
                const data = await response.json();
                if (response.ok && data.length > 0 && data[0].poster) {
                  return { ...item, thumbnail: data[0].poster };
                }
                return { ...item, thumbnail: null }; // Fallback if no poster
              })
            );
            return { ...row, items: updatedItems };
          })
        );
        setInitialRows(updatedRows);
      } catch (err) {
        setError('Failed to fetch initial posters or Telugu movies');
        console.error('Error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchInitialPosters();
  }, []);

  const fetchRecommendations = async (movieTitle) => {
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:5001/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: movieTitle }),
      });
      const data = await response.json();
      if (response.ok) {
        const mappedRecommendations = data.map((rec, i) => ({
          id: i + 100,
          title: rec.title,
          thumbnail: rec.poster || null, // Use TMDB poster from backend
          description: `Language: ${rec.original_language || 'N/A'}, Genres: ${rec.genres || 'N/A'}`,
        }));
        setRecommendations((prev) => ({ ...prev, [movieTitle]: mappedRecommendations }));
      } else {
        setError(data.error || 'Failed to fetch recommendations');
      }
    } catch (err) {
      setError('An error occurred while fetching recommendations');
    } finally {
      setLoading(false);
    }
  };

  const handleMoreInfo = (item) => {
    setSelectedItem(item);
    if (!recommendations[item.title]) {
      fetchRecommendations(item.title);
    }
  };

  return (
    <div className="home">
      <Header />
      <Hero item={featuredItem} onMoreInfo={handleMoreInfo} />
      {initialRows.length > 0 ? initialRows.map((row, index) => (
        <Row key={index} title={row.title} items={row.items} onMoreInfo={handleMoreInfo} />
      )) : initialRowsData.map((row, index) => (
        <Row key={index} title={row.title} items={row.items} onMoreInfo={handleInfo} />
      ))}
      {Object.entries(recommendations).map(([title, recs]) =>
        recs.length > 0 && (
          <Row
            key={`rec-${title}`}
            title={`Recommendations for ${title}`}
            items={recs}
            onMoreInfo={handleMoreInfo}
          />
        )
      )}
      {loading && <div className="loading">Loading recommendations...</div>}
      {error && <div className="error">{error}</div>}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      <Footer />
    </div>
  );
}

export default Home;
