import React, { useState } from 'react';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Search.css';

function Search() {
  const [searchTitle, setSearchTitle] = useState('');
  const [results, setResults] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // TMDB API key (replace with your own key)
  const TMDB_API_KEY = 'YOUR_TMDB_API_KEY'; // e.g., 'abc123def456ghi789'

  const handleSearch = async () => {
    if (!searchTitle.trim()) {
      setError('Please enter a movie title');
      setResults([]);
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await fetch(
        `https://api.themoviedb.org/3/search/movie?api_key=${TMDB_API_KEY}&query=${encodeURIComponent(searchTitle)}`
      );
      const data = await response.json();

      if (response.ok && data.results && data.results.length > 0) {
        const mappedResults = data.results.map((movie, index) => ({
          id: movie.id || index + 100,
          title: movie.title || 'Unknown Title',
          thumbnail: movie.poster_path
            ? `https://image.tmdb.org/t/p/w342${movie.poster_path}`
            : 'https://via.placeholder.com/200x300?text=Poster',
          description: `Language: ${movie.original_language || 'N/A'}, Genres: ${
            movie.genre_ids
              ? movie.genre_ids.join(', ')
              : 'N/A'
          }`,
        }));
        setResults(mappedResults);
      } else {
        setError('No results found');
        setResults([]);
      }
    } catch (err) {
      setError('Network error while searching');
      console.error('Search error:', err);
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  const handleMoreInfo = (item) => {
    setSelectedItem(item);
  };

  return (
    <div className="search">
      <div className="search-container">
        <div className="search-bar">
          <input
            type="text"
            value={searchTitle}
            onChange={(e) => setSearchTitle(e.target.value)}
            placeholder="Search for a movie"
          />
          <button onClick={handleSearch}>Search</button>
        </div>
        {loading && <div className="loading">Loading...</div>}
        {error && <div className="error">{error}</div>}
        {results.length > 0 && (
          <Row
            title={`Search Results for "${searchTitle}"`}
            items={results}
            onMoreInfo={handleMoreInfo}
          />
        )}
        {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      </div>
      <Footer />
    </div>
  );
}

export default Search;
