import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Footer from '../components/Footer';
import './LegalNotices.css';

function LegalNotices() {
  const [notices, setNotices] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    window.scrollTo(0, 0);
    const fetchNotices = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/legal-notices');
        setNotices(response.data);
      } catch (err) {
        setError('Failed to load notices.');
        setNotices([
          { section: 'Copyright', content: '© StreamFlix, Inc.' },
          { section: 'Trademarks', content: 'StreamFlix is a registered trademark.' },
        ]);
      }
    };
    fetchNotices();
  }, []);

  return (
    <div className="legal-notices">
      <main>
        <h1>Legal Notices</h1>
        {error && <p className="error">{error}</p>}
        <div className="content">
          {notices.map((notice, index) => (
            <div key={index}>
              <h3>{notice.section}</h3>
              <p>{notice.content}</p>
            </div>
          ))}
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default LegalNotices;
