import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/Header';
import Footer from '../components/Footer';
import './CorporateInfo.css';

function CorporateInfo() {
  const [info, setInfo] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchInfo = async () => {
      try {
        const response = await axios.get('/api/corporate-info');
        setInfo(response.data);
      } catch (err) {
        setError('Failed to load info.');
        setInfo({
          about: 'StreamFlix, founded in 2020, is a global streaming service.',
          headquarters: '123 Stream Way, LA, CA 90001',
          contact: 'info@streamflix.com',
        });
      }
    };
    fetchInfo();
  }, []);

  return (
    <div className="corporate-info">
      <Header />
      <main>
        <h1>Corporate Information</h1>
        {error && <p className="error">{error}</p>}
        {info && (
          <div className="content">
            <h3>About StreamFlix</h3>
            <p>{info.about}</p>
            <h3>Headquarters</h3>
            <p>{info.headquarters}</p>
            <h3>Contact</h3>
            <p>{info.contact}</p>
          </div>
        )}
      </main>
      <Footer />
    </div>
  );
}

export default CorporateInfo;
