import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Footer from '../components/Footer';
import './AdChoices.css';

function AdChoices() {
  const [personalizedAds, setPersonalizedAds] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    window.scrollTo(0, 0); // Scroll to the top of the page

    const fetchSettings = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/ad-preferences');
        setPersonalizedAds(response.data.personalizedAds);
      } catch (err) {
        setError('Failed to load preferences.');
      }
    };
    fetchSettings();
  }, []);

  const handleToggle = async () => {
    const newValue = !personalizedAds;
    try {
      await axios.post('http://localhost:5001/api/ad-preferences', { personalizedAds: newValue });
      setPersonalizedAds(newValue);
      localStorage.setItem('personalizedAds', newValue);
    } catch (err) {
      setError('Error saving preferences.');
    }
  };

  return (
    <div className="ad-choices">
      <main>
        <h1>Ad Choices</h1>
        <p>Manage your advertising preferences.</p>
        {error && <p className="error">{error}</p>}
        <div className="setting">
          <label>Personalized Ads</label>
          <input
            type="checkbox"
            checked={personalizedAds}
            onChange={handleToggle}
          />
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default AdChoices;
