import React from 'react';
import './Modal.css';

function Modal({ item, onClose }) {
  return (
    <div className="modal">
      <div className="modal-content">
        <i className="fas fa-times modal-close" onClick={onClose} />
        <div className="modal__poster-wrapper">
          <img className="modal__poster" src={item.thumbnail} alt={item.title} />
        </div>
        <div className="modal-details">
          <h2 className="modal-title">{item.title}</h2>
          <p className="modal-description">{item.description}</p>
          <div className="modal-buttons">
            <button className="btn play-btn">
              <i className="fas fa-play" /> Play
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Modal;
