import React from 'react';
import './Home.css';

// Sample movie data
const sampleMovies = [
  {
    id: 1,
    title: 'The Shawshank Redemption',
    image: 'https://via.placeholder.com/200x300?text=Shawshank+Redemption',
    description: 'Two imprisoned men bond over several years, finding solace and redemption through acts of common decency.',
    category: 'movies',
  },
  {
    id: 2,
    title: 'The Godfather',
    image: 'https://via.placeholder.com/200x300?text=The+Godfather',
    description: 'The aging patriarch of an organized crime dynasty transfers control of his clandestine empire to his reluctant son.',
    category: 'movies',
  },
  {
    id: 3,
    title: 'Inception',
    image: 'https://via.placeholder.com/200x300?text=Inception',
    description: 'A thief with the ability to enter dreams uses this skill to steal secrets from within the subconscious.',
    category: 'movies',
  },
  {
    id: 4,
    title: 'Breaking Bad: The Movie',
    image: 'https://via.placeholder.com/200x300?text=Breaking+Bad',
    description: 'A high school chemistry teacher turned methamphetamine kingpin continues his empire in this thrilling spin-off.',
    category: 'tv-shows',
  },
  {
    id: 5,
    title: 'Stranger Things',
    image: 'https://via.placeholder.com/200x300?text=Stranger+Things',
    description: 'A group of kids in a small town encounter supernatural forces and government secrets in the 1980s.',
    category: 'tv-shows',
  },
  {
    id: 6,
    title: 'The Mandalorian',
    image: 'https://via.placeholder.com/200x300?text=The+Mandalorian',
    description: 'A lone bounty hunter protects a young child from the Empire\'s forces in the Star Wars universe.',
    category: 'tv-shows',
  },
];

function Home({ section = 'home' }) {
  const getSectionContent = () => {
    switch (section) {
      case 'home':
        return {
          title: 'FEATURED',
          description: 'Welcome to StreamFlix! Explore the best movies and TV shows.',
          movies: sampleMovies.slice(0, 3), // Show first 3 movies as featured
        };
      case 'tv-shows':
        return {
          title: 'TV SHOWS',
          description: 'Discover our latest TV Shows collection.',
          movies: sampleMovies.filter(movie => movie.category === 'tv-shows'),
        };
      case 'movies':
        return {
          title: 'MOVIES',
          description: 'Check out the hottest Movies now streaming.',
          movies: sampleMovies.filter(movie => movie.category === 'movies'),
        };
      case 'new-popular':
        return {
          title: 'NEW & POPULAR',
          description: 'New & Popular content just for you!',
          movies: sampleMovies.slice(0, 4), // Show top 4 as new/popular
        };
      case 'my-list':
        return {
          title: 'MY LIST',
          description: 'Your personalized list of favorites.',
          movies: sampleMovies.slice(2, 5), // Example subset for my list
        };
      default:
        return {
          title: 'FEATURED',
          description: 'Welcome to StreamFlix!',
          movies: sampleMovies.slice(0, 3),
        };
    }
  };

  const { title, description, movies } = getSectionContent();

  return (
    <div className="home-container">
      <h1 className="home-title">{title}</h1>
      <p className="home-description">{description}</p>
      <div className="movie-grid">
        {movies.map(movie => (
          <div key={movie.id} className="movie-card">
            <img src={movie.image} alt={movie.title} className="movie-image" />
            <h3 className="movie-title">{movie.title}</h3>
            <p classDescription="movie-description">{movie.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
