import React from 'react';
import './Tile.css';

function Tile({ item, onMoreInfo, isFavorited = false, toggleFavorite }) {
  // Check if the thumbnail is black.jpg or missing
  const isBlackPoster = !item.thumbnail || item.thumbnail.includes('black.jpg');
  const firstLetter = item.title ? item.title.charAt(0).toUpperCase() : '';

  return (
    <div
      className={`tile ${isBlackPoster ? 'tile-no-poster' : ''}`}
      style={{ backgroundImage: `url(${item.thumbnail || ''})` }}
      role="button"
      tabIndex={0}
      onClick={() => onMoreInfo(item)}
      onKeyPress={(e) => e.key === 'Enter' && onMoreInfo(item)}
      aria-label={`View details for ${item.title}`}
    >
      {isBlackPoster && (
        <span className="tile-first-letter">{firstLetter}</span>
      )}
      <div className="tile-overlay">
        <p className="tile-title">{item.title}</p>
        <div className="tile-buttons">
          <i className="fas fa-play tile-btn play" aria-label="Play" />
          <i
            className="fas fa-info-circle tile-btn info"
            aria-label="More info"
            onClick={(e) => {
              e.stopPropagation();
              onMoreInfo(item);
            }}
          />
          <button
            className={`favorite-button ${isFavorited ? 'favorited' : ''}`}
            onClick={(e) => {
              e.stopPropagation();
              toggleFavorite(item);
            }}
            aria-label={isFavorited ? 'Remove from favorites' : 'Add to favorites'}
          >
            {isFavorited ? '‚ù§Ô∏è' : 'ü§ç'}
          </button>
        </div>
      </div>
    </div>
  );
}

export default Tile;
