import React, { useState, useEffect, useRef } from 'react';
import './Hero.css';

function Hero({ item, onMoreInfo }) {
  const [showVideo, setShowVideo] = useState(false);
  const videoRef = useRef(null);

  useEffect(() => {
    console.log('Hero item:', item); // Debug item props
    if (item && item.video) {
      console.log('Starting 3-second delay for video');
      const timer = setTimeout(() => {
        console.log(`Attempting to play video for "${item.title}"`);
        setShowVideo(true);
      }, 3000);
      return () => clearTimeout(timer);
    } else {
      console.warn('No video provided for item:', item);
    }
  }, [item]);

  useEffect(() => {
    if (showVideo && videoRef.current) {
      console.log('Video element ready, attempting play');
      videoRef.current.play().catch((err) => {
        console.error('Video playback failed:', err);
      });
    }
  }, [showVideo]);

  if (!item) return null;

  return (
    <div
      className="hero"
      style={{
        backgroundImage: !showVideo && item.thumbnail ? `url(${item.thumbnail})` : 'none'
      }}
    >
      {showVideo && item.video ? (
        <video
          ref={videoRef}
          src={item.video}
          autoPlay
          loop
          playsInline
          className="hero-video"
          onError={(e) => console.error(`Video error for ${item.title}:`, e)}
          onCanPlay={() => console.log('Video can play')}
          onPlay={() => console.log('Video is playing')}
        >
          <source src={item.video} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      ) : showVideo ? (
        <div className="video-fallback">
          <img src={item.thumbnail} alt="Video failed" />
          <p>Video failed to load</p>
        </div>
      ) : null}
      <div className="hero-content">
        <h1 className="hero-title">{item.title}</h1>
        <p className="hero-description">{item.description}</p>
        <div className="hero-buttons">
          <button className="btn info-btn" onClick={() => onMoreInfo(item)}>
            <i className="fas fa-info-circle" /> More Info
          </button>
        </div>
      </div>
    </div>
  );
}

export default Hero;
