import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Chart from 'chart.js/auto';
import Header from '../components/Header';
import Footer from '../components/Footer';
import './SpeedTest.css';

function SpeedTest() {
  const [speed, setSpeed] = useState(null);
  const [latency, setLatency] = useState(null);
  const [testing, setTesting] = useState(false);
  const [error, setError] = useState('');
  const [history, setHistory] = useState(() => {
    const saved = localStorage.getItem('speedTestHistory');
    return saved ? JSON.parse(saved) : [];
  });
  const [chartData, setChartData] = useState([]);

  // Update local storage when history changes
  useEffect(() => {
    localStorage.setItem('speedTestHistory', JSON.stringify(history));
  }, [history]);

  // Initialize chart
  useEffect(() => {
    const ctx = document.getElementById('speedChart')?.getContext('2d');
    if (ctx) {
      const chart = new Chart(ctx, {
        type: 'line',
        data: {
          labels: chartData.map((_, i) => `Sample ${i + 1}`),
          datasets: [{
            label: 'Speed (Mbps)',
            data: chartData,
            borderColor: '#ff0000',
            backgroundColor: 'rgba(255, 0, 0, 0.2)',
            fill: true,
          }],
        },
        options: {
          scales: {
            y: { beginAtZero: true, title: { display: true, text: 'Speed (Mbps)' } },
            x: { title: { display: true, text: 'Sample' } },
          },
        },
      });
      return () => chart.destroy();
    }
  }, [chartData]);

  const runSpeedTest = async () => {
    setTesting(true);
    setSpeed(null);
    setLatency(null);
    setError('');
    setChartData([]);

    try {
      // Measure latency (ping)
      const pingStart = performance.now();
      await axios.get('/');
      const pingEnd = performance.now();
      const pingMs = (pingEnd - pingStart).toFixed(2);
      setLatency(pingMs);

      // Run multiple samples
      const samples = 3;
      const speeds = [];
      for (let i = 0; i < samples; i++) {
        const startTime = performance.now();
        const response = await axios.get('/files/100MB.bin', {
          responseType: 'blob',
        });
        const endTime = performance.now();
        const duration = (endTime - startTime) / 1000; // seconds
        const fileSize = response.data.size / 1024 / 1024; // MB
        const speedMbps = (fileSize * 8 / duration).toFixed(2); // Mbps
        speeds.push(parseFloat(speedMbps));
        setChartData((prev) => [...prev, parseFloat(speedMbps)]);
      }

      // Average speed
      const avgSpeed = (speeds.reduce((a, b) => a + b, 0) / speeds.length).toFixed(2);
      setSpeed(avgSpeed);

      // Update history
      const result = {
        speed: avgSpeed,
        latency: pingMs,
        timestamp: new Date().toLocaleString(),
      };
      setHistory((prev) => [result, ...prev].slice(0, 5)); // Keep last 5 results
    } catch (err) {
      setError('Failed to run speed test.');
    }
    setTesting(false);
  };

  // Calculate needle rotation and gauge offset (0-100 Mbps maps to 0-360 deg)
  const needleAngle = speed ? (parseFloat(speed) / 100) * 360 : 0;
  const gaugeOffset = speed ? (1 - parseFloat(speed) / 100) * 439.8 : 439.8;

  // Generate tick marks and labels
  const ticks = [];
  for (let i = 0; i <= 100; i += 5) {
    const isMajor = i % 20 === 0;
    const angle = (i / 100) * 360;
    const rad = (angle * Math.PI) / 180;
    const innerRadius = isMajor ? 55 : 60;
    const outerRadius = isMajor ? 45 : 50;
    const x1 = 100 + innerRadius * Math.cos(rad);
    const y1 = 100 + innerRadius * Math.sin(rad);
    const x2 = 100 + outerRadius * Math.cos(rad);
    const y2 = 100 + outerRadius * Math.sin(rad);
    ticks.push(
      <line
        key={`tick-${i}`}
        x1={x1}
        y1={y1}
        x2={x2}
        y2={y2}
        stroke="#ffffff"
        strokeWidth={isMajor ? 2 : 1}
      />
    );
    if (isMajor) {
      const labelX = 100 + 35 * Math.cos(rad);
      const labelY = 100 + 35 * Math.sin(rad);
      ticks.push(
        <text
          key={`label-${i}`}
          x={labelX}
          y={labelY}
          fill="#ffffff"
          fontSize="10"
          textAnchor="middle"
          dominantBaseline="middle"
        >
          {i}
        </text>
      );
    }
  }

  return (
    <div className="speed-test">
      <Header />
      <main>
        <h1>Speed Test</h1>
        <p>Test your internet speed for optimal StreamFlix streaming.</p>
        <div className="speedometer">
          <svg viewBox="0 0 200 200">
            <circle
              cx="100"
              cy="100"
              r="70"
              fill="none"
              stroke="#1a1a1a"
              strokeWidth="20"
            />
            <circle
              className={testing || speed ? 'gauge' : ''}
              cx="100"
              cy="100"
              r="70"
              fill="none"
              stroke="#ff0000"
              strokeWidth="20"
              strokeDasharray="439.8"
              strokeDashoffset={testing ? '0' : gaugeOffset}
            />
            {ticks}
            <line
              className={testing || speed ? 'needle' : ''}
              x1="100"
              y1="100"
              x2="100"
              y2="30"
              stroke="#ffffff"
              strokeWidth="4"
              style={{ transform: `rotate(${needleAngle}deg)`, transformOrigin: '100px 100px' }}
            />
          </svg>
          <div className="speed-display">
            {testing ? 'Testing...' : speed ? `${speed} Mbps` : 'Click to test'}
          </div>
        </div>
        <div className="metrics">
          {latency && <p>Latency: {latency} ms</p>}
          {speed && <p>Download Speed: {speed} Mbps</p>}
        </div>
        <button onClick={runSpeedTest} disabled={testing}>
          {testing ? 'Testing...' : 'Run Speed Test'}
        </button>
        {error && <p className="error">{error}</p>}
        <div className="chart-container">
          <h3>Speed Test Results</h3>
          <canvas id="speedChart" width="400" height="200"></canvas>
        </div>
        {history.length > 0 && (
          <div className="history">
            <h3>Test History</h3>
            <table>
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Speed (Mbps)</th>
                  <th>Latency (ms)</th>
                </tr>
              </thead>
              <tbody>
                {history.map((entry, index) => (
                  <tr key={index}>
                    <td>{entry.timestamp}</td>
                    <td>{entry.speed}</td>
                    <td>{entry.latency}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </main>
      <Footer />
    </div>
  );
}

export default SpeedTest;
