import React, { useState, useEffect } from 'react';
import Footer from '../components/Footer';
import './CorporateInfo.css';

function CorporateInfo() {
  const [info, setInfo] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    window.scrollTo(0, 0);
    const fetchInfo = async () => {
      try {
        // Simulating backend fetch — replace with actual API when available
        throw new Error('Simulating no backend');
      } catch (err) {
        setError('Failed to load info.');
        setInfo({
          about: 'StreamFlix, founded in 2020, is a global streaming service offering a vast collection of entertainment across genres.',
          headquarters: '123 Stream Way, Los Angeles, CA 90001',
          contact: 'info@streamflix.com',
          mission: 'To deliver world-class entertainment to audiences globally with seamless access anytime, anywhere.',
          leadership: 'Led by CEO Jane Doe and a diverse executive team with decades of media and technology experience.',
          globalPresence: 'Operating in over 100 countries with localized content and support in more than 20 languages.'
        });
      }
    };
    fetchInfo();
  }, []);

  return (
    <div className="corporate-info">
      <main>
        <h1>Corporate Information</h1>
        {error && <p className="error">{error}</p>}
        {info && (
          <div className="info-container">
            <section id="about">
              <h3>About StreamFlix</h3>
              <p>{info.about}</p>
            </section>
            <section id="headquarters">
              <h3>Headquarters</h3>
              <p>{info.headquarters}</p>
            </section>
            <section id="contact">
              <h3>Contact</h3>
              <p>{info.contact}</p>
            </section>
            <section id="mission">
              <h3>Our Mission</h3>
              <p>{info.mission}</p>
            </section>
            <section id="leadership">
              <h3>Leadership</h3>
              <p>{info.leadership}</p>
            </section>
            <section id="global-presence">
              <h3>Global Presence</h3>
              <p>{info.globalPresence}</p>
            </section>
          </div>
        )}
      </main>
      <Footer />
    </div>
  );
}

export default CorporateInfo;
