import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Footer from '../components/Footer';
import './MediaCenter.css';

function MediaCenter() {
  const [articles, setArticles] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    window.scrollTo(0, 0);
    const fetchArticles = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/news');
        setArticles(response.data);
      } catch (err) {
        setError('Failed to load articles.');
        setArticles([
          { id: 1, title: 'New Series Announced', date: '2025-04-01', content: 'A thrilling series.' },
          { id: 2, title: 'Global Expansion', date: '2025-03-15', content: 'Now in 10 new countries.' },
        ]);
      }
    };
    fetchArticles();
  }, []);

  return (
    <div className="media-center">
      <main>
        <h1>Media Center</h1>
        <p>Latest news from StreamFlix.</p>
        {error && <p className="error">{error}</p>}
        <div className="articles">
          {articles.map((article) => (
            <div key={article.id} className="article">
              <h3>{article.title}</h3>
              <p className="date">{article.date}</p>
              <p>{article.content}</p>
            </div>
          ))}
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default MediaCenter;
