import React from 'react';

function Row({ title, items, onMoreInfo }) {
  return (
    <div className="row">
      <h2 className="row-title">{title}</h2>
      <div className="row-posters">
        {items.map((item) => (
          <img
            key={item.id}
            src={item.thumbnail || 'https://via.placeholder.com/200x300'} // Fallback image
            alt={item.title}
            className="row-poster"
            onClick={() => onMoreInfo(item)}
            style={{ cursor: 'pointer' }}
          />
        ))}
      </div>
    </div>
  );
}

export default Row;
