import React from 'react';
import './Tile.css';

function Tile({ item, onMoreInfo }) {
  // Fallback thumbnail if item.thumbnail is null or invalid
  const thumbnailUrl = item.thumbnail || 'https://via.placeholder.com/200x300';

  return (
    <div
      className="tile"
      style={{ backgroundImage: `url(${thumbnailUrl})` }}
      role="button"
      tabIndex={0}
      onClick={() => onMoreInfo(item)}
      onKeyPress={(e) => e.key === 'Enter' && onMoreInfo(item)}
      aria-label={`View details for ${item.title}`}
    >
      <div className="tile-overlay">
        <p className="tile-title">{item.title}</p>
        <div className="tile-buttons">
          <i className="fas fa-play tile-btn play" aria-label="Play" />
          <i
            className="fas fa-info-circle tile-btn info"
            aria-label="More info"
            onClick={(e) => {
              e.stopPropagation(); // Prevent triggering the outer onClick
              onMoreInfo(item);
            }}
          />
        </div>
      </div>
    </div>
  );
}

export default Tile;
