import React, { useState, useEffect, useRef } from 'react';
import './Hero.css';

function Hero({ item, onMoreInfo }) {
  const [showVideo, setShowVideo] = useState(false);
  const videoRef = useRef(null);

  useEffect(() => {
    if (item?.video) {
      const timer = setTimeout(() => {
        setShowVideo(true);
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [item]);

  useEffect(() => {
    const handleUserInteraction = () => {
      if (videoRef.current) {
        videoRef.current.muted = false;
        videoRef.current.volume = 1.0;
        videoRef.current.play().catch((err) => {
          console.warn('User interaction play error:', err);
        });
      }
      window.removeEventListener('click', handleUserInteraction);
    };

    if (showVideo) {
      window.addEventListener('click', handleUserInteraction);
    }

    return () => {
      window.removeEventListener('click', handleUserInteraction);
    };
  }, [showVideo]);

  if (!item) return null;

  return (
    <div
      className="hero"
      style={{
        backgroundImage: !showVideo && item.thumbnail ? `url(${item.thumbnail})` : 'none',
      }}
    >
      {showVideo && item.video ? (
        <video
          ref={videoRef}
          src={item.video}
          autoPlay
          loop
          playsInline
          muted // ðŸ”‡ start muted
          className="hero-video"
          onError={(e) => console.error(`Video error for ${item.title}:`, e)}
        >
          <source src={item.video} type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      ) : null}

      <div className="hero-content">
        <h1 className="hero-title">{item.title}</h1>
        <p className="hero-description">{item.description}</p>
        <div className="hero-buttons">
          <button className="btn info-btn" onClick={() => onMoreInfo(item)}>
            <i className="fas fa-info-circle" /> More Info
          </button>
        </div>
      </div>
    </div>
  );
}

export default Hero;
