import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Footer from '../components/Footer';
import './Terms.css';

function Terms() {
  const [terms, setTerms] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    window.scrollTo(0, 0);
    const fetchTerms = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/terms');
        setTerms(response.data);
      } catch (err) {
        setError('Failed to load terms. Showing default content.');
        setTerms([
          { section: '1. Acceptance', content: 'By using StreamFlix, you agree to these terms of use.' },
          { section: '2. Subscription', content: 'Subscriptions can be monthly or yearly. You can cancel anytime in your account settings.' },
          { section: '3. Content Usage', content: 'All content is for personal use only. Unauthorized distribution or sharing is prohibited.' },
          { section: '4. Account Security', content: 'You are responsible for your account credentials. Notify us immediately of any breach.' },
          { section: '5. Termination', content: 'Violation of terms may result in account suspension or termination without notice.' },
          { section: '6. Modifications', content: 'Terms may be updated anytime. Continued usage implies acceptance of any changes.' },
          { section: '7. Support', content: 'Contact support via our help center or email for any issues.' },
          { section: '8. Refunds', content: 'Refunds are granted in accordance with our refund policy available on the site.' },
          { section: '9. Licensing', content: 'Content is licensed, not sold. All rights reserved to respective owners.' },
          { section: '10. Jurisdiction', content: 'These terms are governed by applicable laws in your jurisdiction.' },
        ]);
      }
    };
    fetchTerms();
  }, []);

  return (
    <div className="terms">
      <main>
        <h1>Terms of Use</h1>
        {error && <p className="error">{error}</p>}
        <div className="terms-scroll">
          {terms.map((term, index) => (
            <div key={index} className="term-block">
              <h3>{term.section}</h3>
              <p>{term.content}</p>
            </div>
          ))}
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default Terms;
