import React, { useState } from 'react';
import Header from '../components/Header';
import Hero from '../components/Hero';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Home.css';

function Home() {
  const [selectedItem, setSelectedItem] = useState(null);

  const featuredItem = {
    id: 1,
    title: "The Witcher",
    description: "Geralt of Rivia, a mutated monster-hunter for hire, journeys toward his destiny.",
    thumbnail: "https://images.unsplash.com/photo-1600585154340-be6161a56a0c?ixlib=rb-4.0.3&auto=format&fit=crop&w=1920&q=80",
  };

  const rows = [
    {
      title: "Trending Now",
      items: [
        { id: 2, title: "Stranger Things", thumbnail: "https://images.unsplash.com/photo-1626814026160-2237a95fc5a0?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80" },
        { id: 3, title: "The Crown", thumbnail: "https://images.unsplash.com/photo-1577083753625-7e1ddce8bd94?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80" },
        { id: 4, title: "Mindhunter", thumbnail: "https://images.unsplash.com/photo-1509343256512-d77a5cb3791b?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80" },
      ],
    },
    {
      title: "Watch It Again",
      items: [
        { id: 5, title: "Breaking Bad", thumbnail: "https://images.unsplash.com/photo-1600585154340-be6161a56a0c?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80" },
        { id: 6, title: "Narcos", thumbnail: "https://images.unsplash.com/photo-1577083753625-7e1ddce8bd94?ixlib=rb-4.0.3&auto=format&fit=crop&w=300&q=80" },
      ],
    },
  ];

  return (
    <div className="home">
      <Header />
      <Hero item={featuredItem} onMoreInfo={setSelectedItem} />
      {rows.map((row, index) => (
        <Row key={index} title={row.title} items={row.items} onMoreInfo={setSelectedItem} />
      ))}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      <Footer />
    </div>
  );
}

export default Home;
