import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Hero from '../components/Hero';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Home.css';

function Home() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [recommendations, setRecommendations] = useState({});
  const [initialRows, setInitialRows] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const featuredItem = {
    id: 271726, // TMDB ID for Baahubali: The Beginning
    title: "Baahubali: The Beginning",
    description: "A Telugu epic about valor and sacrifice.",
    thumbnail: "/data/baahubali_the_beginning.jpg", // Local poster
    video: "/data/baahubali_trailer.mp4" // Assuming a trailer video exists in data folder
  };

  const initialRowsData = [
    {
      title: "Trending Movies",
      items: [
        { id: 900352, title: "Kalki 2898 AD", thumbnail: "/data/kalki_2898_ad.jpg", description: "A sci-fi epic set in a dystopian future." },
        { id: 934632, title: "Salaar", thumbnail: "/data/salaar.jpg", description: "An action-packed saga of power and betrayal." },
        { id: 0, title: "Chaava", thumbnail: "/data/chaava.jpg", description: "A historical drama about bravery." },
        { id: 614933, title: "RRR", thumbnail: "/data/rrr.jpg", description: "A revolutionary tale of friendship and rebellion." },
        { id: 614934, title: "Pushpa 2: The Rule", thumbnail: "/data/pushpa_2_the_rule.jpg", description: "A gripping sequel to the smuggling saga." },
        { id: 1121402, title: "Hi Nanna", thumbnail: "/data/hi_nanna.jpg", description: "A heartwarming family drama." },
        { id: 1, title: "Mad2", thumbnail: "/data/mad2.jpg", description: "A thrilling continuation of mystery." },
        { id: 297282, title: "Court", thumbnail: "/data/court.jpg", description: "A thought-provoking legal drama." },
        { id: 1560, title: "Return of the Dragon", thumbnail: "/data/return_of_the_dragon.jpg", description: "A classic martial arts adventure." },
        { id: 2, title: "Saripoda Sanivaaram", thumbnail: "/data/saripoda_sanivaaram.jpg", description: "An intense vigilante story." },
        { id: 614932, title: "Saaho", thumbnail: "/data/saaho.jpg", description: "A high-octane action thriller." },
      ],
    },
    {
      title: "Watch It Again",
      items: [
        { id: 5, title: "Inception", thumbnail: null, description: "A mind-bending heist movie." },
        { id: 6, title: "Titanic", thumbnail: null, description: "A romantic disaster film." },
      ],
    },
  ];

  // Fetch posters for non-local items and recommendations on mount
  useEffect(() => {
    const fetchInitialPosters = async () => {
      setLoading(true);
      try {
        const updatedRows = await Promise.all(
          initialRowsData.map(async (row) => {
            const updatedItems = await Promise.all(
              row.items.map(async (item) => {
                if (item.thumbnail) {
                  return item; // Skip items with local posters
                }
                const response = await fetch('http://localhost:5001/recommend', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify({ movie_title: item.title }),
                });
                const data = await response.json();
                if (response.ok && data.length > 0 && data[0].poster) {
                  return { ...item, thumbnail: data[0].poster };
                }
                return { ...item, thumbnail: "https://via.placeholder.com/200x300" };
              })
            );
            return { ...row, items: updatedItems };
          })
        );
        setInitialRows(updatedRows);

        // Fetch recommendations for Baahubali to ensure they appear
        await fetchRecommendations("Baahubali: The Beginning");
      } catch (err) {
        setError('Failed to fetch posters or recommendations');
        console.error('Error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchInitialPosters();
  }, []);

  const fetchRecommendations = async (movieTitle) => {
    if (recommendations[movieTitle]) return; // Skip if already fetched
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:5001/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: movieTitle }),
      });
      const data = await response.json();
      if (response.ok && Array.isArray(data)) {
        const mappedRecommendations = data.map((rec, i) => ({
          id: i + 100,
          title: rec.title,
          thumbnail: rec.poster || "https://via.placeholder.com/200x300",
          description: `Language: ${rec.original_language || 'N/A'}, Genres: ${rec.genres || 'N/A'}`,
        }));
        setRecommendations((prev) => ({ ...prev, [movieTitle]: mappedRecommendations }));
      } else {
        setError(data.error || 'Failed to fetch recommendations');
      }
    } catch (err) {
      setError('An error occurred while fetching recommendations');
      console.error('Error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleMoreInfo = (item) => {
    setSelectedItem({
      ...item,
      thumbnail: item.title === "Baahubali: The Beginning" ? "/data/baahubali_the_beginning.jpg" : item.thumbnail
    });
    fetchRecommendations(item.title);
  };

  return (
    <div className="home">
      <Header />
      <Hero item={featuredItem} onMoreInfo={handleMoreInfo} />
      {initialRows.length > 0 ? initialRows.map((row, index) => (
        <Row key={index} title={row.title} items={row.items} onMoreInfo={handleMoreInfo} />
      )) : initialRowsData.map((row, index) => (
        <Row key={index} title={row.title} items={row.items} onMoreInfo={handleMoreInfo} />
      ))}
      {Object.entries(recommendations).map(([title, recs]) =>
        recs.length > 0 && (
          <Row
            key={`rec-${title}`}
            title={`Recommendations for ${title}`}
            items={recs}
            onMoreInfo={handleMoreInfo}
          />
        )
      )}
      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error">{error}</div>}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      <Footer />
    </div>
  );
}

export default Home;
