import React, { useState, useEffect, useRef } from 'react';
import './Hero.css';

function Hero({ item, onMoreInfo }) {
  const [showVideo, setShowVideo] = useState(false);
  const [videoError, setVideoError] = useState(false);
  const [isMuted, setIsMuted] = useState(true); // Start muted for autoplay compliance
  const videoRef = useRef(null);

  useEffect(() => {
    console.log('Hero item:', item);
    if (!item || !item.video) {
      console.warn('No video provided for item:', item);
      return;
    }
    console.log('Starting 3-second delay for video:', item.video);
    const timer = setTimeout(() => {
      console.log(`Attempting to play video for "${item.title}"`);
      setShowVideo(true);
    }, 3000);
    return () => {
      console.log('Clearing timer');
      clearTimeout(timer);
    };
  }, [item]);

  useEffect(() => {
    if (showVideo && videoRef.current && !videoError) {
      console.log('Video element ready, attempting play');
      videoRef.current.play().catch((err) => {
        console.error('Video playback failed:', err);
        setVideoError(true);
      });
    }
  }, [showVideo, videoError]);

  const toggleMute = () => {
    if (videoRef.current) {
      try {
        const newMutedState = !isMuted;
        videoRef.current.muted = newMutedState;
        setIsMuted(newMutedState);
        console.log(`Video ${newMutedState ? 'muted' : 'unmuted'} for "${item.title}"`);
      } catch (err) {
        console.error('Failed to toggle mute:', err);
      }
    } else {
      console.warn('Video ref not available for mute toggle');
    }
  };

  if (!item) return null;

  return (
    <div
      className="hero"
      style={{
        backgroundImage: !showVideo || videoError ? `url(${item.thumbnail})` : 'none',
      }}
    >
      {showVideo && item.video && !videoError ? (
        <div className="video-container">
          <video
            ref={videoRef}
            src={item.video}
            autoPlay
            muted={isMuted}
            loop
            playsInline
            className="hero-video"
            onError={(e) => {
              console.error(`Video error for ${item.title}:`, e);
              setVideoError(true);
            }}
            onCanPlay={() => console.log('Video can play')}
            onPlay={() => console.log('Video is playing')}
          />
          <button
            className="mute-toggle-btn"
            onClick={toggleMute}
            title={isMuted ? 'Unmute' : 'Mute'}
            aria-label={isMuted ? 'Unmute video' : 'Mute video'}
          >
            <i className={`fas ${isMuted ? 'fa-volume-mute' : 'fa-volume-up'}`} />
          </button>
        </div>
      ) : showVideo && (videoError || !item.video) ? (
        <div className="video-fallback">
          <img src={item.thumbnail} alt="Video failed" />
          <p>Video failed to load</p>
        </div>
      ) : null}
      <div className="hero-content">
        <h1 className="hero-title">{item.title}</h1>
        <p className="hero-description">{item.description}</p>
        <div className="hero-buttons">
          <button className="btn info-btn" onClick={() => onMoreInfo(item)}>
            <i className="fas fa-info-circle" /> More Info
          </button>
        </div>
      </div>
    </div>
  );
}

export default Hero;
