import React from 'react';
import Tile from './Tile';
import './Row.css';

function Row({ title, items, onMoreInfo }) {
  return (
    <div className="row">
      <h2 className="row-title">{title}</h2>
      <div className="tiles">
        <div className="slider-arrow left-arrow">
          <i className="fas fa-chevron-left arrow-icon" />
        </div>
        {items.map(item => (
          <Tile key={item.id} item={item} onMoreInfo={onMoreInfo} />
        ))}
        <div className="slider-arrow right-arrow">
          <i className="fas fa-chevron-right arrow-icon" />
        </div>
      </div>
    </div>
  );
}

export default Row;
