import React, { useState, useEffect } from 'react';
import axios from 'axios';
// import Header from '../components/Header';
import Footer from '../components/Footer';
import './Terms.css';

function Terms() {
  const [terms, setTerms] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    window.scrollTo(0, 0);
    const fetchTerms = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/terms');
        setTerms(response.data);
      } catch (err) {
        setError('Failed to load terms from the server. Displaying default terms.');
        setTerms([
          {
            section: '1. Acceptance of Terms',
            content: 'By accessing or using StreamFlix, you agree to be bound by these Terms of Use and our Privacy Policy.'
          },
          {
            section: '2. Subscription & Billing',
            content: 'StreamFlix offers monthly and annual subscriptions. Payments are processed securely and are non-refundable once billed.'
          },
          {
            section: '3. User Responsibilities',
            content: 'You are responsible for maintaining the confidentiality of your account credentials and for all activities under your account.'
          },
          {
            section: '4. Content Ownership',
            content: 'All content provided on StreamFlix, including movies, images, and branding, are owned or licensed by StreamFlix and protected by intellectual property laws.'
          },
          {
            section: '5. Restrictions',
            content: 'You may not share your account, redistribute our content, or use StreamFlix for any illegal or unauthorized purposes.'
          },
          {
            section: '6. Modifications',
            content: 'We reserve the right to update or change these terms at any time. Continued use of the platform constitutes acceptance of those changes.'
          },
          {
            section: '7. Termination',
            content: 'We may suspend or terminate your account if you violate any of these terms without prior notice.'
          },
          {
            section: '8. Contact Us',
            content: 'If you have any questions or concerns about these Terms, please contact us at support@streamflix.com.'
          },
        ]);
      }
    };
    fetchTerms();
  }, []);

  return (
    <div className="terms">
      {/* <Header /> */}
      <main>
        <h1>Terms of Use</h1>
        {error && <p className="error">{error}</p>}
        <div className="content">
          {terms.map((term, index) => (
            <div className="term-block" key={index}>
              <h3>{term.section}</h3>
              <p>{term.content}</p>
            </div>
          ))}
        </div>
      </main>
      <Footer />
    </div>
  );
}

export default Terms;
