import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Papa from 'papaparse';
// import Header from '../components/Header';
import Footer from '../components/Footer';
import './Privacy.css';

function Privacy() {
  const [policy, setPolicy] = useState([]);
  const [activeSection, setActiveSection] = useState(null);
  const [selectedCsv, setSelectedCsv] = useState(null);
  const [csvData, setCsvData] = useState(null);

  useEffect(() => {
    window.scrollTo(0, 0);
    const fetchPolicy = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/privacy');
        setPolicy(response.data);
      } catch (err) {
        // Silently use fallback data
        setPolicy([
          {
            section: 'Privacy Collection',
            content: (
              <div>
                <h4>Types of Data Collected</h4>
                <p>
                  Our platform collects various types of personal information to enhance your user experience. This includes email addresses for account management, user preferences for tailored recommendations, and browsing history to understand your content interests. We may also collect demographic details, such as age or location, if you provide them voluntarily during profile setup. Interaction data, such as clicks, search queries, and time spent on specific movies, is tracked to improve service quality and personalization.
                </p>
                <h4>Consent and User Control</h4>
                <p>
                  All data collection is conducted with explicit user consent, obtained through clear opt-in mechanisms at the point of data entry. We provide transparent information about why we collect data, such as improving recommendations or analyzing usage trends. You can manage your privacy preferences, opt out of non-essential data collection, or revoke consent at any time through account settings. Our practices comply with GDPR, CCPA, and other global privacy regulations.
                </p>
                <h4>Data Security Measures</h4>
                <p>
                  We prioritize data security by implementing robust measures. Data in transit is protected with SSL/TLS encryption, and data at rest is stored using AES-256 encryption. Access to personal information is restricted to authorized personnel who receive regular training on data protection. We conduct periodic security audits and maintain an incident response plan to address potential breaches, ensuring prompt notification to users and authorities as required by law.
                </p>
                <h4>User Rights and Access</h4>
                <p>
                  You have the right to access, correct, or delete your personal data. Requests are processed within 30 days, and you can download your data in a machine-readable format for portability. For users under 16 (or the applicable age of consent), we require verified parental consent for data collection. Our platform avoids automated decision-making that significantly affects users without human oversight, ensuring fairness and transparency.
                </p>
                <h4>Data Sharing Policies</h4>
                <p>
                  We do not share personal data with third parties for marketing purposes without your explicit consent. Anonymized, aggregated data may be shared with trusted partners, such as analytics providers or cloud services, to improve our platform. For example, we might share that “75% of users enjoy action movies,” but this data cannot identify individuals. You can opt out of data sharing at any time via your account settings.
                </p>
                <h4>Data Minimization and Support</h4>
                <p>
                  We adhere to data minimization, collecting only what is necessary for our services. For instance, we do not require phone numbers unless relevant to a specific feature. Anonymous browsing is available, though it limits personalization. Our Data Protection Officer is available 24/7 at privacy@platform.com, and our FAQ section addresses common privacy concerns. We regularly update our privacy policy and notify users of changes via email or in-app alerts.
                </p>
                <h4>Commitment to Privacy</h4>
                <p>
                  Our commitment is to empower you with control over your personal information while delivering a high-quality experience. We strive to balance personalization with privacy, ensuring you can enjoy our services with confidence. If you have questions or need assistance, our support team is ready to help, and our privacy practices are continually refined to meet evolving standards and user expectations.
                </p>
              </div>
            ),
          },
          {
            section: 'Data Collection',
            content:
              'We collect data from movies.csv (movie metadata: movieId, title, genres), credits.csv (cast and crew: movieId, cast, crew), and TeluguMovies_enhanced.csv (Telugu movie metadata: movieId, title, genres, language) to analyze movie trends and improve recommendations. Data is sourced from internal datasets and anonymized to protect user privacy.',
          },
          {
            section: 'Data Usage',
            content: (
              <div>
                <h4>Tailored Recommendations</h4>
                <p>
                  We use your data to provide personalized movie recommendations based on your interactions, such as search queries, clicks, and viewing history. For example, if you frequently watch Telugu action movies, our algorithms prioritize similar titles. This personalization enhances your experience by surfacing content that aligns with your preferences, making discovery seamless and engaging.
                </p>
                <h4>Platform Optimization</h4>
                <p>
                  Data drives improvements to our platform’s functionality and user interface. We analyze aggregate usage patterns to optimize search accuracy, refine filtering options, and enhance navigation. For instance, identifying popular genres helps us curate featured sections, while user feedback informs new features like personalized playlists or advanced search tools.
                </p>
                <h4>Analytics and Insights</h4>
                <p>
                  We generate anonymized reports on platform usage, such as genre popularity or regional trends, to guide development and support market research. These reports, like “70% of users prefer comedy,” contain no personal data. Analytics partners receive only anonymized data to improve recommendation algorithms, ensuring your privacy is protected.
                </p>
                <h4>Secure Data Processing</h4>
                <p>
                  All data is processed in a secure environment with SSL/TLS encryption for transit and AES-256 encryption for storage. Access is restricted to authorized personnel, and we conduct regular security assessments to mitigate risks. Our robust infrastructure ensures your data remains safe during processing, analysis, and storage.
                </p>
                <h4>Fraud Prevention and Compliance</h4>
                <p>
                  We analyze usage patterns to detect and prevent fraudulent activity, such as unauthorized account access. Minimal data is shared with partners like payment processors for compliance purposes, adhering to strict privacy standards. If you notice suspicious activity, our support team is available to assist promptly.
                </p>
                <h4>Fair and Responsible Use</h4>
                <p>
                  We practice data minimization, retaining data only as long as necessary (e.g., 90 days for browsing history). You can request deletion within 30 days, per GDPR/CCPA. Our algorithms are audited for fairness to avoid biases, and we avoid significant automated decisions without human review. Contact privacy@platform.com for concerns.
                </p>
                <h4>Continuous Improvement</h4>
                <p>
                  We conduct A/B testing and surveys using anonymized data to evaluate new features, with participation optional. Research into recommendation technologies uses aggregated data to advance our services without compromising privacy. Our goal is to use data responsibly to deliver a secure, personalized experience while respecting your rights.
                </p>
              </div>
            ),
          },
        ]);
      }
    };
    fetchPolicy();
  }, []);

  const fetchCsvData = (filename) => {
    // Programmatically generate 500 rows for each CSV
    let csv = '';
    if (filename === 'movies.csv') {
      csv = 'movieId,title,genres\n';
      for (let i = 1; i <= 500; i++) {
        const genres = i % 3 === 0 ? 'Action|Adventure' : i % 2 === 0 ? 'Drama|Romance' : 'Comedy|Family';
        csv += `${i},Movie ${i} (202${i % 10}),${genres}\n`;
      }
    } else if (filename === 'credits.csv') {
      csv = 'movieId,cast,crew\n';
      for (let i = 1; i <= 500; i++) {
        csv += `${i},Actor ${i}|Co-Actor ${i},Director ${i}\n`;
      }
    } else {
      csv = 'movieId,title,genres,language\n';
      for (let i = 1; i <= 500; i++) {
        const genres = i % 3 === 0 ? 'Action|Drama' : i % 2 === 0 ? 'Comedy|Romance' : 'Drama|Thriller';
        csv += `${i},Telugu Movie ${i} (202${i % 10}),${genres},Telugu\n`;
      }
    }
    const parsed = Papa.parse(csv, { header: true, skipEmptyLines: true });
    setCsvData({ headers: parsed.meta.fields, data: parsed.data.map(Object.values) });
  };

  const handleNavClick = (section) => {
    setActiveSection(section);
    setSelectedCsv(null);
    setCsvData(null);
  };

  const handleCsvClick = (filename) => {
    setSelectedCsv(filename);
    fetchCsvData(filename);
  };

  const handleBackClick = () => {
    setSelectedCsv(null);
    setCsvData(null);
  };

  const renderSidePanel = () => {
    if (!activeSection && !selectedCsv) return null;

    if (selectedCsv && csvData) {
      return (
        <div className="side-panel">
          <div className="side-panel-header">
            <button className="back-button" onClick={handleBackClick}>
              <i className="fas fa-arrow-left"></i> Back
            </button>
            <h3>{selectedCsv}</h3>
          </div>
          <div className="table-container">
            <table className="csv-table">
              <thead>
                <tr>
                  {csvData.headers.map((header, index) => (
                    <th key={index}>{header}</th>
                  ))}
                </tr>
              </thead>
              <tbody>
                {csvData.data.map((row, rowIndex) => (
                  <tr key={rowIndex}>
                    {row.map((cell, cellIndex) => (
                      <td key={cellIndex}>{cell}</td>
                    ))}
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      );
    }

    if (activeSection === 'Data Collection') {
      return (
        <div className="side-panel">
          <h3>Data Collection</h3>
          <p>Click a file to view its contents:</p>
          <ul>
            <li>
              <a href="#" onClick={() => handleCsvClick('movies.csv')}>
                movies.csv
              </a>
            </li>
            <li>
              <a href="#" onClick={() => handleCsvClick('credits.csv')}>
                credits.csv
              </a>
            </li>
            <li>
              <a href="#" onClick={() => handleCsvClick('TeluguMovies_enhanced.csv')}>
                TeluguMovies_enhanced.csv
              </a>
            </li>
          </ul>
        </div>
      );
    }

    const sectionData = policy.find((item) => item.section === activeSection);
    return (
      <div className="side-panel">
        <h3>{activeSection}</h3>
        {sectionData ? sectionData.content : <p>No details available.</p>}
      </div>
    );
  };

  return (
    <div className="privacy">
      {/* <Header /> */}
      <nav className="privacy-nav">
        <a
          href="#"
          className={activeSection === 'Privacy Collection' ? 'active' : ''}
          onClick={() => handleNavClick('Privacy Collection')}
        >
          Privacy Collection
        </a>
        <a
          href="#"
          className={activeSection === 'Data Collection' ? 'active' : ''}
          onClick={() => handleNavClick('Data Collection')}
        >
          Data Collection
        </a>
        <a
          href="#"
          className={activeSection === 'Data Usage' ? 'active' : ''}
          onClick={() => handleNavClick('Data Usage')}
        >
          Data Usage
        </a>
      </nav>
      <main></main>
      {renderSidePanel()}
      <Footer />
    </div>
  );
}

export default Privacy;
