import React, { useState } from 'react';
import axios from 'axios';
import Header from '../components/Header';
import Footer from '../components/Footer';
import './SpeedTest.css';

function SpeedTest() {
  const [speed, setSpeed] = useState(null);
  const [testing, setTesting] = useState(false);
  const [error, setError] = useState('');

  const runSpeedTest = async () => {
    setTesting(true);
    setSpeed(null);
    setError('');
    try {
      const startTime = performance.now();
      const response = await axios.get('/files/100MB.bin', {
        responseType: 'blob',
      });
      const endTime = performance.now();
      const duration = (endTime - startTime) / 1000; // seconds
      const fileSize = response.data.size / 1024 / 1024; // MB
      const speedMbps = (fileSize * 8 / duration).toFixed(2); // Mbps
      setSpeed(speedMbps);
    } catch (err) {
      setError('Failed to run speed test.');
    }
    setTesting(false);
  };

  return (
    <div className="speed-test">
      <Header />
      <main>
        <h1>Speed Test</h1>
        <p>Test your internet speed for streaming.</p>
        <div className="speedometer">
          <svg viewBox="0 0 200 100">
            <path
              d="M 20 80 A 60 60 0 0 1 180 80"
              fill="none"
              stroke="#222"
              strokeWidth="20"
            />
            <path
              className={testing ? 'gauge' : ''}
              d="M 20 80 A 60 60 0 0 1 180 80"
              fill="none"
              stroke="#ff00ff"
              strokeWidth="20"
              strokeDasharray="251.2"
              strokeDashoffset={testing ? '0' : '251.2'}
            />
            <line
              className={testing ? 'needle' : ''}
              x1="100"
              y1="80"
              x2="100"
              y2="20"
              stroke="#00ffff"
              strokeWidth="4"
            />
          </svg>
          <div className="speed-display">
            {testing ? 'Testing...' : speed ? `${speed} Mbps` : 'Click to test'}
          </div>
        </div>
        <button onClick={runSpeedTest} disabled={testing}>
          {testing ? 'Testing...' : 'Run Speed Test'}
        </button>
        {error && <p className="error">{error}</p>}
      </main>
      <Footer />
    </div>
  );
}

export default SpeedTest;
