import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Hero from '../components/Hero';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Home.css';

// Only import the poster for Baahubali (used in the Hero section)
import baahubaliPoster from '../data/bb2.jpg';

function Home() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [recommendations, setRecommendations] = useState({});
  const [initialRows, setInitialRows] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [favorites, setFavorites] = useState(() => JSON.parse(localStorage.getItem('myList') || '[]'));
  const [notification, setNotification] = useState('');

  const featuredItem = {
    id: 271726,
    title: "Baahubali: The Beginning",
    description: "A Telugu epic about valor and sacrifice.",
    thumbnail: baahubaliPoster,
    video: "/videos/bb2.mp4"
  };

  const initialRowsData = [
    {
      title: "Watch It Again",
      items: [
        { id: 5, title: "Inception", thumbnail: null, description: "A mind-bending heist movie." },
        { id: 6, title: "Titanic", thumbnail: null, description: "A romantic disaster film." },
      ],
    },
  ];

  useEffect(() => {
    window.scrollTo(0, 0);

    localStorage.setItem('myList', JSON.stringify(favorites));
    console.log('Favorites saved to localStorage:', favorites);
  }, [favorites]);

  const toggleFavorite = (item) => {
    setFavorites((prevFavorites) => {
      const isFavorited = prevFavorites.some((fav) => fav.id === item.id);
      if (isFavorited) {
        console.log('Removing from favorites:', item.title);
        const updatedFavorites = prevFavorites.filter((fav) => fav.id !== item.id);
        setNotification(`"${item.title}" removed from favorites`);
        setTimeout(() => setNotification(''), 3000);
        return updatedFavorites;
      } else {
        setNotification(`"${item.title}" added to favorites`);
        setTimeout(() => setNotification(''), 3000);
        console.log('Adding to favorites:', item.title);
        return [...prevFavorites, item];
      }
    });
  };

  useEffect(() => {
    const fetchInitialData = async () => {
      setLoading(true);
      try {
        // Fetch "Trending Movies" dynamically using /recommend
        const trendingMoviesResponse = await fetch('http://localhost:5000/recommend', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ movie_title: "Kalki 2898 AD" }), // Use a Telugu movie to ensure Telugu recommendations
        });
        const trendingMoviesData = await trendingMoviesResponse.json();
        const trendingMovies = trendingMoviesData.map((rec, i) => ({
          id: i + 300,
          title: rec.title,
          thumbnail: rec.poster || null,
          description: `Language: ${rec.original_language || 'te'}, Genres: ${rec.genres || 'N/A'}`,
        }));

        const updatedRows = await Promise.all(
          initialRowsData.map(async (row) => {
            const updatedItems = await Promise.all(
              row.items.map(async (item) => {
                if (item.thumbnail) return item;
                try {
                  const response = await fetch('http://localhost:5000/recommend', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ movie_title: item.title }),
                  });
                  const data = await response.json();
                  if (response.ok && data.length > 0 && data[0].poster) {
                    return { ...item, thumbnail: data[0].poster };
                  }
                  return { ...item, thumbnail: null };
                } catch (err) {
                  console.error(`Failed to fetch poster for ${item.title}:`, err);
                  return { ...item, thumbnail: null };
                }
              })
            );
            return { ...row, items: updatedItems };
          })
        );

        // Add the "Trending Movies" row with fetched recommendations
        setInitialRows([{ title: "Trending Movies", items: trendingMovies }, ...updatedRows]);
        await fetchRecommendations("Baahubali: The Beginning");
      } catch (err) {
        setError('Failed to fetch initial data');
        console.error('Initial fetch error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchInitialData();
  }, []);

  const fetchRecommendations = async (movieTitle) => {
    setLoading(true);
    setError(null);
    try {
      console.log(`Fetching recommendations for "${movieTitle}"...`);
      const response = await fetch('http://localhost:5000/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: movieTitle }),
      });
      const data = await response.json();
      console.log(`Response for /recommend: status ${response.status}, data:`, data);
      if (response.ok && Array.isArray(data) && data.length > 0) {
        // Select a random number of recommendations (between 3 and 8)
        const numRecommendations = Math.floor(Math.random() * (8 - 3 + 1)) + 3;
        const shuffledData = data.sort(() => Math.random() - 0.5); // Shuffle the array
        const selectedRecommendations = shuffledData.slice(0, numRecommendations);

        const mappedRecommendations = selectedRecommendations.map((rec, i) => ({
          id: i + 100,
          title: rec.title,
          thumbnail: rec.poster || null,
          description: `Language: ${rec.original_language || 'te'}, Genres: ${rec.genres || 'N/A'}`,
        }));
        setRecommendations((prev) => ({ ...prev, [movieTitle]: mappedRecommendations }));
      } else {
        console.log(`No valid data from /recommend for "${movieTitle}", falling back to /random-telugu...`);
        await fetchRandomTeluguRecommendations(movieTitle);
      }
    } catch (err) {
      console.error(`Network error for "${movieTitle}":`, err);
      await fetchRandomTeluguRecommendations(movieTitle);
    } finally {
      setLoading(false);
    }
  };

  const fetchRandomTeluguRecommendations = async (movieTitle) => {
    try {
      console.log(`Attempting fetch from /random-telugu for "${movieTitle}" with GET...`);
      const response = await fetch('http://localhost:5000/random-telugu', {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
      });
      console.log(`/random-telugu response: status ${response.status}, statusText: ${response.statusText}, data:`, await response.clone().json());
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}, statusText: ${response.statusText}`);
      }
      const data = await response.json();
      if (Array.isArray(data)) {
        console.log(`Received ${data.length} random Telugu recommendations for "${movieTitle}":`, data);
        // Select a random number of recommendations (between 3 and 8)
        const numRecommendations = Math.floor(Math.random() * (8 - 3 + 1)) + 3;
        const shuffledData = data.sort(() => Math.random() - 0.5); // Shuffle the array
        const selectedRecommendations = shuffledData.slice(0, numRecommendations);

        const randomRecommendations = selectedRecommendations.map((rec, i) => ({
          id: i + 200,
          title: rec.title,
          thumbnail: rec.poster || null,
          description: `Language: ${rec.original_language || 'te'}, Genres: ${rec.genres || 'N/A'}`,
        }));
        setRecommendations((prev) => ({ ...prev, [movieTitle]: randomRecommendations }));
      } else {
        throw new Error('Invalid data format from /random-telugu endpoint');
      }
    } catch (err) {
      console.error(`Error fetching random Telugu recommendations for "${movieTitle}":`, err);
      setError(`Network error fetching random recommendations: ${err.message}`);
      setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
    }
  };

  const handleMoreInfo = (item) => {
    setSelectedItem(item);
    fetchRecommendations(item.title);
  };

  return (
    <div className="home">
      {/* <Header /> */}
      <Hero
        item={featuredItem}
        onMoreInfo={handleMoreInfo}
        favorites={favorites || []}
        toggleFavorite={toggleFavorite}
      />
      {(initialRows.length > 0 ? initialRows : initialRowsData).map((row, index) => (
        <Row
          key={index}
          title={row.title}
          items={row.items}
          onMoreInfo={handleMoreInfo}
          favorites={favorites || []}
          toggleFavorite={toggleFavorite}
        />
      ))}
      {Object.entries(recommendations).map(([title, recs]) => (
        <Row
          key={`rec-${title}`}
          title={`Recommendations for ${title}`}
          items={recs}
          onMoreInfo={handleMoreInfo}
          favorites={favorites || []}
          toggleFavorite={toggleFavorite}
        />
      ))}
      {!Object.keys(recommendations).length && !loading && (
        <div className="no-recommendations">No recommendations loaded yet.</div>
      )}
      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error">{error}</div>}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      {notification && <div className="notification">{notification}</div>}
      <Footer />
    </div>
  );
}

export default Home;
