import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Hero from '../components/Hero';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Home.css';

import kalkiPoster from '../data/Kalki.jpg';
import salaarPoster from '../data/salaar.jpg';
import chaavaPoster from '../data/chaava.jpg';
import rrrPoster from '../data/rrr.jpg';
import pushpa2Poster from '../data/pushpa2.jpg';
import hiNannaPoster from '../data/hinanna.jpg';
import mad2Poster from '../data/mad2.jpg';
import courtPoster from '../data/court.jpg';
import dragonPoster from '../data/rod.jpg';
import saripodaPoster from '../data/ss.jpg';
import saahoPoster from '../data/saaho.jpg';
import baahubaliPoster from '../data/bb2.jpg';
import baahubaliVideo from '../data/bb2.mp4';

function Home() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [recommendations, setRecommendations] = useState({});
  const [initialRows, setInitialRows] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const featuredItem = {
    id: 271726,
    title: "Baahubali: The Beginning",
    description: "A Telugu epic about valor and sacrifice.",
    thumbnail: baahubaliPoster,
    video: baahubaliVideo,
  };

  console.log('Video path:', featuredItem.video);

  const initialRowsData = [
    {
      title: "Trending Movies",
      items: [
        { id: 900352, title: "Kalki 2898 AD", thumbnail: kalkiPoster, description: "A sci-fi epic set in a dystopian future." },
        { id: 934632, title: "Salaar", thumbnail: salaarPoster, description: "An action-packed saga of power and betrayal." },
        { id: 0, title: "Chaava", thumbnail: chaavaPoster, description: "A historical drama about bravery." },
        { id: 614933, title: "RRR", thumbnail: rrrPoster, description: "A revolutionary tale of friendship and rebellion." },
        { id: 614934, title: "Pushpa 2: The Rule", thumbnail: pushpa2Poster, description: "A gripping sequel to the smuggling saga." },
        { id: 1121402, title: "Hi Nanna", thumbnail: hiNannaPoster, description: "A heartwarming family drama." },
        { id: 1, title: "Mad2", thumbnail: mad2Poster, description: "A thrilling continuation of mystery." },
        { id: 297282, title: "Court", thumbnail: courtPoster, description: "A thought-provoking legal drama." },
        { id: 1560, title: "Return of the Dragon", thumbnail: dragonPoster, description: "A classic martial arts adventure." },
        { id: 2, title: "Saripoda Sanivaaram", thumbnail: saripodaPoster, description: "An intense vigilante story." },
        { id: 614932, title: "Saaho", thumbnail: saahoPoster, description: "A high-octane action thriller." },
      ],
    },
    {
      title: "Watch It Again",
      items: [
        { id: 5, title: "Inception", thumbnail: null, description: "A mind-bending heist movie." },
        { id: 6, title: "Titanic", thumbnail: null, description: "A romantic disaster film." },
      ],
    },
  ];

  useEffect(() => {
    const fetchInitialData = async () => {
      setLoading(true);
      try {
        const updatedRows = await Promise.all(
          initialRowsData.map(async (row) => {
            const updatedItems = await Promise.all(
              row.items.map(async (item) => {
                if (item.thumbnail) return item;
                try {
                  const response = await fetch('http://localhost:5001/recommend', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ movie_title: item.title }),
                  });
                  const data = await response.json();
                  if (response.ok && data.length > 0 && data[0].poster) {
                    return { ...item, thumbnail: data[0].poster };
                  }
                  return { ...item, thumbnail: "https://via.placeholder.com/200x300" };
                } catch (err) {
                  console.error(`Failed to fetch poster for ${item.title}:`, err);
                  return { ...item, thumbnail: "https://via.placeholder.com/200x300" };
                }
              })
            );
            return { ...row, items: updatedItems };
          })
        );
        setInitialRows(updatedRows);
        await fetchRecommendations("Baahubali: The Beginning");
      } catch (err) {
        setError('Failed to fetch initial data');
        console.error('Initial fetch error:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchInitialData();
  }, []);

  const fetchRecommendations = async (movieTitle) => {
    console.log(`Fetching recommendations for "${movieTitle}"`);
    setLoading(true);
    setError(null);
    try {
      const response = await fetch('http://localhost:5001/recommend', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ movie_title: movieTitle }),
      });
      const data = await response.json();
      if (response.ok && Array.isArray(data)) {
        const mappedRecommendations = data.map((rec, i) => ({
          id: i + 100,
          title: rec.title,
          thumbnail: rec.poster || "https://via.placeholder.com/200x300",
          description: `Language: ${rec.original_language || 'N/A'}, Genres: ${rec.genres || 'N/A'}`,
        }));
        setRecommendations(prev => ({ ...prev, [movieTitle]: mappedRecommendations }));
      } else {
        setError(data.error || 'No recommendations available');
      }
    } catch (err) {
      setError('Network error');
      console.error('Network error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleMoreInfo = (item) => {
    console.log(`More Info: ${item.title}`);
    setSelectedItem({
      ...item,
      thumbnail: item.title === "Baahubali: The Beginning" ? baahubaliPoster : item.thumbnail,
    });
    fetchRecommendations(item.title);
  };

  return (
    <div className="home">
      <Header />
      <Hero item={featuredItem} onMoreInfo={handleMoreInfo} />
      {initialRows.length > 0 ? initialRows.map((row, index) => (
        <Row key={index} title={row.title} items={row.items} onMoreInfo={handleMoreInfo} />
      )) : initialRowsData.map((row, index) => (
        <Row key={index} title={row.title} items={row.items} onMoreInfo={handleMoreInfo} />
      ))}
      {Object.entries(recommendations).map(([title, recs]) => (
        <Row
          key={`rec-${title}`}
          title={`Recommendations for ${title}`}
          items={recs}
          onMoreInfo={handleMoreInfo}
        />
      ))}
      {Object.keys(recommendations).length === 0 && !loading && (
        <div className="no-recommendations">No recommendations loaded yet.</div>
      )}
      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error">{error}</div>}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      <Footer />
    </div>
  );
}

export default Home;
