import React from 'react';
import './Tile.css';

function Tile({ item, onMoreInfo }) {
  return (
    <div className="tile" style={{ backgroundImage: `url(${item.thumbnail})` }}>
      <div className="tile-overlay">
        <p className="tile-title">{item.title}</p>
        <div className="tile-buttons">
          <i className="fas fa-play tile-btn play" />
          <i className="fas fa-info-circle tile-btn info" onClick={() => onMoreInfo(item)} />
        </div>
      </div>
    </div>
  );
}

export default Tile;
