import React, { useState, useEffect, useRef } from 'react';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Search.css';

function Search() {
  const [searchTitle, setSearchTitle] = useState('');
  const [results, setResults] = useState([]);
  const [suggestions, setSuggestions] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const suggestionCache = useRef(new Map());
  const debounceTimeout = useRef(null);

  const fetchWithTimeout = async (url, options, timeout = 10000) => {
    const controller = new AbortController();
    const id = setTimeout(() => controller.abort(), timeout);
    try {
      const response = await fetch(url, { ...options, signal: controller.signal });
      clearTimeout(id);
      return response;
    } catch (err) {
      clearTimeout(id);
      throw err;
    }
  };

  const fetchSuggestions = async (query) => {
    if (!query.trim()) {
      setSuggestions([]);
      return;
    }

    if (suggestionCache.current.has(query)) {
      setSuggestions(suggestionCache.current.get(query));
      return;
    }

    try {
      const response = await fetchWithTimeout(
        'http://localhost:5000/autocomplete',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ query }),
        },
        5000
      );

      if (!response.ok) {
        throw new Error(`HTTP error: ${response.status}`);
      }

      const data = await response.json();
      suggestionCache.current.set(query, data);
      setSuggestions(data);
    } catch (err) {
      console.error('Autocomplete error:', err);
      setSuggestions([]);
    }
  };

  const handleInputChange = (e) => {
    const query = e.target.value;
    setSearchTitle(query);
    setResults([]); // Clear previous results

    if (debounceTimeout.current) {
      clearTimeout(debounceTimeout.current);
    }

    debounceTimeout.current = setTimeout(() => {
      fetchSuggestions(query);
    }, 300);
  };

  const handleInputFocus = () => {
    setSuggestions([]); // Clear suggestions on focus
    setResults([]); // Clear results on focus
  };

  const handleSuggestionClick = (suggestion) => {
    setSearchTitle(suggestion.title);
    setSuggestions([]);
    handleSearch(suggestion.title);
  };

  const handleSearch = async (title = searchTitle) => {
    if (!title.trim()) {
      setError('Please enter a movie title');
      setResults([]);
      setSuggestions([]);
      return;
    }

    setLoading(true);
    setError(null);
    setSuggestions([]);

    try {
      const response = await fetchWithTimeout(
        'http://localhost:5000/recommend',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ movie_title: title }),
        },
        10000
      );

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || `HTTP error: ${response.status}`);
      }

      const data = await response.json();

      if (Array.isArray(data) && data.length > 0) {
        // Add exact match first
        const exactMatch = suggestionCache.current
          .get(title.toLowerCase().trim())?.find(
            (s) => s.title.toLowerCase() === title.toLowerCase()
          ) || { title, id: 301344, poster: 'https://image.tmdb.org/t/p/w500/9gm3lL8n3dZA9EKGTHZdt16qMKD.jpg' }; // Fallback for Baahubali
        const mappedResults = [
          {
            id: exactMatch.id || 301344,
            title: exactMatch.title,
            thumbnail: exactMatch.poster || 'https://image.tmdb.org/t/p/w500/9gm3lL8n3dZA9EKGTHZdt16qMKD.jpg',
            description: `Language: te, Genres: Action Drama`,
          },
          ...data.map((rec, i) => ({
            id: rec.id || i + 100,
            title: rec.title || 'Unknown Title',
            thumbnail: rec.poster || 'https://via.placeholder.com/200x300?text=Poster',
            description: `Language: ${rec.original_language || 'N/A'}, Genres: ${rec.genres || 'N/A'}`,
          })),
        ];

        setResults(mappedResults.slice(0, 6)); // Exact match + 5 recommendations
      } else {
        setError(data.error || 'No results found');
        setResults([]);
      }
    } catch (err) {
      setError(`Network error: ${err.message}`);
      console.error('Search error:', err);
      setResults([]);
    } finally {
      setLoading(false);
    }
  };

  const handleMoreInfo = (item) => {
    setSelectedItem(item);
  };

  // Clear suggestions when clicking outside
  const handleClickOutside = (e) => {
    if (!e.target.closest('.search-bar') && !e.target.closest('.suggestions')) {
      setSuggestions([]);
    }
  };

  useEffect(() => {
    document.addEventListener('click', handleClickOutside);
    return () => document.removeEventListener('click', handleClickOutside);
  }, []);

  return (
    <div className="search">
      <div className="search-container">
        <div className="search-bar">
          <input
            type="text"
            value={searchTitle}
            onChange={handleInputChange}
            onFocus={handleInputFocus}
            placeholder="Search for a movie"
          />
          <button onClick={() => handleSearch()}>Search</button>
          {suggestions.length > 0 && (
            <ul className="suggestions">
              {suggestions.map((suggestion) => (
                <li
                  key={suggestion.id}
                  className="suggestion-item"
                  onClick={() => handleSuggestionClick(suggestion)}
                >
                  <img
                    src={suggestion.poster}
                    alt={suggestion.title}
                    className="suggestion-poster"
                  />
                  <span>{suggestion.title}</span>
                </li>
              ))}
            </ul>
          )}
        </div>
        {loading && <div className="loading">Loading...</div>}
        {error && <div className="error">{error}</div>}
        {results.length > 0 && (
          <Row
            title={`Search Results for "${searchTitle}"`}
            items={results}
            onMoreInfo={handleMoreInfo}
          />
        )}
        {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      </div>
      <Footer />
    </div>
  );
}

export default Search;
