import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Hero from '../components/Hero';
import Row from '../components/Row';
import Modal from '../components/Modal';
import Footer from '../components/Footer';
import './Home.css';

// Import posters
import kalkiPoster from '../data/Kalki.jpg';
import salaarPoster from '../data/salaar.jpg';
import chaavaPoster from '../data/chaava.jpg';
import rrrPoster from '../data/rrr.jpg';
import pushpa2Poster from '../data/pushpa2.jpg';
import hiNannaPoster from '../data/hinanna.jpg';
import mad2Poster from '../data/mad2.jpg';
import courtPoster from '../data/court.jpg';
import dragonPoster from '../data/rod.jpg';
import saripodaPoster from '../data/ss.jpg';
import saahoPoster from '../data/saaho.jpg';
import baahubaliPoster from '../data/bb2.jpg';
import blackPoster from '../data/black.jpg';

function Home() {
  const [selectedItem, setSelectedItem] = useState(null);
  const [recommendations, setRecommendations] = useState(() => {
    const saved = sessionStorage.getItem('recommendations');
    return saved ? JSON.parse(saved) : {};
  });
  const [initialRows, setInitialRows] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [favorites, setFavorites] = useState(() =>
    JSON.parse(localStorage.getItem('myList') || '[]')
  );
  const [notification, setNotification] = useState('');

  const featuredItem = {
    id: 301344,
    title: 'Baahubali: The Beginning',
    description: 'A Telugu epic about valor and sacrifice.',
    thumbnail: baahubaliPoster,
    video: '/videos/bb2.mp4',
    language: 'te',
    genres: ['Action', 'Drama']
  };

  const initialRowsData = [
    {
      title: 'Trending Movies',
      items: [
        { id: 900352, title: 'Kalki 2898 AD', thumbnail: kalkiPoster, description: 'A futuristic film inspired by Hindu mythology.', language: 'te', genres: ['Sci-Fi', 'Mythology', 'Action'] },
        { id: 934632, title: 'Salaar: Part 1 – Ceasefire', thumbnail: salaarPoster, description: 'An action-packed saga of power and betrayal.', language: 'te', genres: ['Action', 'Drama', 'Epic'] },
        { id: 0, title: 'Chhaava', thumbnail: chaavaPoster, description: 'A historical drama about bravery.', language: 'hi', genres: ['Historical', 'Action', 'Drama'] },
        { id: 614933, title: 'RRR', thumbnail: rrrPoster, description: 'A revolutionary tale of friendship and rebellion.', language: 'te', genres: ['Action', 'Historical', 'Drama', 'Epic'] },
        { id: 614934, title: 'Pushpa 2: The Rule', thumbnail: pushpa2Poster, description: 'A gripping sequel to the smuggling saga.', language: 'te', genres: ['Action', 'Drama'] },
        { id: 1121402, title: 'Hi Nanna', thumbnail: hiNannaPoster, description: 'A heartwarming family drama.', language: 'te', genres: ['Romance', 'Drama'] },
        { id: 1, title: 'Mad2', thumbnail: mad2Poster, description: 'A thrilling continuation of mystery.', language: 'te', genres: ['Comedy', 'Drama', 'Coming-of-Age'] },
        { id: 297282, title: 'Court', thumbnail: courtPoster, description: 'A thought-provoking legal drama.', language: 'mr', genres: ['Drama', 'Legal'] },
        { id: 1560, title: 'Return of the Dragon', thumbnail: dragonPoster, description: 'A classic martial arts adventure.', language: 'en', genres: ['Action', 'Comedy', 'Martial Arts'] },
        { id: 614932, title: 'Saaho', thumbnail: saahoPoster, description: 'A high-octane action thriller.', language: 'te', genres: ['Action', 'Thriller'] },
        { id: 2, title: 'Saripodhaa Sanivaaram', thumbnail: saripodaPoster, description: 'An intense vigilante story.', language: 'te', genres: ['Action', 'Thriller', 'Vigilante'] },
        { id: 301345, title: 'Baahubali 2: The Conclusion', thumbnail: baahubaliPoster, description: 'The epic conclusion of a warrior’s saga.', language: 'te', genres: ['Action', 'Drama'] }
      ]
    },
    {
      title: 'Watch It Again',
      items: [
        { id: 5, title: 'Inception', thumbnail: blackPoster, description: 'A mind-bending heist movie.', language: 'en', genres: ['Sci-Fi', 'Thriller'] },
        { id: 6, title: 'Titanic', thumbnail: blackPoster, description: 'A romantic disaster film.', language: 'en', genres: ['Romance', 'Drama'] }
      ]
    }
  ];

  const staticMovieTitles = initialRowsData[0].items.map(item => item.title);

  useEffect(() => {
    const handleBeforeUnload = () => {
      console.log("Clearing recommendations from sessionStorage");
      sessionStorage.removeItem('recommendations');
    };
    window.addEventListener('beforeunload', handleBeforeUnload);
    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, []);

  useEffect(() => {
    window.scrollTo(0, 0);
    localStorage.setItem('myList', JSON.stringify(favorites));
  }, [favorites]);

  useEffect(() => {
    sessionStorage.setItem('recommendations', JSON.stringify(recommendations));
  }, [recommendations]);

  const toggleFavorite = (item) => {
    setFavorites((prevFavorites) => {
      const isFavorited = prevFavorites.some((fav) => fav.id === item.id);
      if (isFavorited) {
        setNotification(`"${item.title}" removed from favorites`);
        setTimeout(() => setNotification(''), 3000);
        return prevFavorites.filter((fav) => fav.id !== item.id);
      } else {
        setNotification(`"${item.title}" added to favorites`);
        setTimeout(() => setNotification(''), 3000);
        return [...prevFavorites, item];
      }
    });
  };

  const fetchRecommendations = async (movieTitle, genres, isTelugu = false) => {
    setLoading(true);
    setError(null);
    console.log(`Fetching recommendations for "${movieTitle}" (isTelugu: ${isTelugu}, genres: ${genres.join(', ')})`);
    try {
      const numRecs = 5;
      const endpoint = isTelugu ? '/random-telugu-by-genre' : '/recommend';
      const body = isTelugu
        ? { genres, exclude_titles: staticMovieTitles, num_movies: numRecs }
        : { movie_title: movieTitle };

      console.log(`Requesting ${endpoint} with body:`, body);
      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body)
      });
      console.log(`Response status: ${response.status}`);
      if (!response.ok) {
        const errorText = await response.text();
        throw new Error(`HTTP error! status: ${response.status}, message: ${errorText}`);
      }
      const data = await response.json();
      console.log(`Received data:`, data);
      if (Array.isArray(data) && data.length > 0) {
        const uniqueData = Array.from(new Map(data.map((item) => [item.title.toLowerCase(), item])).values());
        const mappedRecommendations = uniqueData.slice(0, numRecs).map((rec, i) => {
          const poster = rec.poster && rec.poster !== "https://via.placeholder.com/200x300" ? rec.poster : null;
          return {
            id: rec.id || (i + 100),
            title: rec.title,
            thumbnail: poster,
            description: `Language: ${rec.original_language || 'en'}, Genres: ${rec.genres || 'N/A'}`,
            language: rec.original_language || 'en',
            genres: rec.genres ? rec.genres.split(' ') : []
          };
        });
        setRecommendations((prev) => {
          const updated = { ...prev, [movieTitle]: mappedRecommendations };
          console.log(`Set recommendations for "${movieTitle}":`, mappedRecommendations);
          return updated;
        });
      } else {
        console.warn(`No valid recommendations for "${movieTitle}"`);
        setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
      }
    } catch (err) {
      console.error(`Error fetching recommendations for "${movieTitle}":`, err);
      setError(`Error fetching recommendations: ${err.message}`);
      setRecommendations((prev) => ({ ...prev, [movieTitle]: [] }));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    const fetchInitialData = async () => {
      setLoading(true);
      try {
        console.log("Fetching initial data...");
        const updatedRows = JSON.parse(JSON.stringify(initialRowsData));
        setInitialRows(updatedRows);

        if (featuredItem.language === 'te' && !recommendations[featuredItem.title]) {
          console.log(`Fetching initial recommendations for "${featuredItem.title}"`);
          await fetchRecommendations(featuredItem.title, featuredItem.genres, true);
        }
      } catch (err) {
        console.error("Initial fetch error:", err);
        setError('Failed to fetch initial data');
      } finally {
        setLoading(false);
      }
    };

    fetchInitialData();
  }, []);

  const handleMoreInfo = (item) => {
    console.log(`Opening modal for "${item.title}"`);
    setSelectedItem(item);
    if (!recommendations[item.title]) {
      fetchRecommendations(item.title, item.genres, item.language === 'te');
    }
  };

  return (
    <div className="home">
      <Header />
      <Hero
        item={featuredItem}
        onMoreInfo={handleMoreInfo}
        favorites={favorites}
        toggleFavorite={toggleFavorite}
      />
      {(initialRows.length > 0 ? initialRows : initialRowsData).map((row, index) => (
        <Row
          key={index}
          title={row.title}
          items={row.items}
          onMoreInfo={handleMoreInfo}
          favorites={favorites}
          toggleFavorite={toggleFavorite}
        />
      ))}
      {Object.entries(recommendations).map(([title, recs]) => (
        recs.length > 0 && (
          <Row
            key={`rec-${title}`}
            title={`Recommendations for ${title}`}
            items={recs}
            onMoreInfo={handleMoreInfo}
            favorites={favorites}
            toggleFavorite={toggleFavorite}
          />
        )
      ))}
      {loading && <div className="loading">Loading...</div>}
      {error && <div className="error">{error}</div>}
      {selectedItem && <Modal item={selectedItem} onClose={() => setSelectedItem(null)} />}
      {notification && <div className="notification">{notification}</div>}
      <Footer />
    </div>
  );
}

export default Home;
